import { NextRequest, NextResponse } from "next/server";
import { getInternalApiUrl } from "@/lib/backend-config";

/**
 * Skills Subscriptions API Proxy
 * ä»£ç† /api/v1/skills/subscriptions è¯·æ±‚åˆ°åç«¯
 */

export async function GET(request: NextRequest) {
  try {
    const backendUrl = getInternalApiUrl("/api/v1/skills/subscriptions");

    // è½¬å‘è¯·æ±‚åˆ°åç«¯
    const headers: Record<string, string> = {
      "Content-Type": "application/json",
    };

    // è½¬å‘ Authorization header
    const auth = request.headers.get("Authorization");
    if (auth) {
      headers["Authorization"] = auth;
    }

    const response = await fetch(backendUrl, {
      method: "GET",
      headers,
    });

    const data = await response.json();

    return NextResponse.json(data, { status: response.status });
  } catch (error) {
    console.error("Skills subscriptions API error:", error);
    return NextResponse.json(
      { error: "Failed to fetch subscriptions", detail: String(error) },
      { status: 500 }
    );
  }
}

