"use client";

/**
 * Legacy Chat Page - 重定向到对应 skill 的 chat 页面
 * 保留此页面以支持旧链接
 */

import { useEffect } from "react";
import { useRouter } from "next/navigation";
import { useSkill } from "@/providers";
import { LuminousPaper, VibeGlyph } from "@/components/core";

export default function LegacyChatPage() {
  const router = useRouter();
  const { skill } = useSkill();

  useEffect(() => {
    // 重定向到对应 skill 的 chat 页面
    router.replace(`/${skill}/chat`);
  }, [router, skill]);

  // 显示加载状态
  return (
    <LuminousPaper skill={skill} variant="default" className="min-h-screen">
      <div className="flex items-center justify-center min-h-screen">
        <VibeGlyph size="md" skill={skill} showAura={true} animate={true} />
      </div>
    </LuminousPaper>
  );
}

