"use client";

import { useRef, useEffect, useMemo, useCallback } from "react";
import type { UIMessage } from "@ai-sdk/react";
import { ChatMessage } from "./ChatMessage";
import { ChatInput } from "./ChatInput";
import { InsightCard, InsightType } from "@/components/insight/InsightCard";
import { VibeGlyph, BreathAura, type SkillType } from "@/components/core";
import { DailyGreeting } from "@/components/greeting/DailyGreeting";
import { useVibeChat, type SkillId } from "@/hooks/useVibeChat";

interface InsightData {
  id: string;
  insight_type: InsightType;
  title: string;
  content: string;
}

/**
 * Extract text content from AI SDK 6 UIMessage
 * UIMessage uses parts array instead of content string
 */
function getMessageContent(message: UIMessage): string {
  if (!message.parts || message.parts.length === 0) {
    // Fallback for messages without parts
    return (message as unknown as { content?: string }).content || '';
  }

  // Concatenate all text parts
  return message.parts
    .filter((part): part is { type: 'text'; text: string } => part.type === 'text')
    .map(part => part.text)
    .join('');
}

export type VoiceMode = "warm" | "sarcastic";

export interface ChatContainerProps {
  skillId: string;
  skill?: SkillType;
  conversationId?: string;
  voiceMode?: VoiceMode;
  onConversationStart?: (id: string) => void;
}

// ═══════════════════════════════════════════════════════════════════════════
// ChatEmptyState - LUMINOUS PAPER Design System
// 空状态：居中 VibeGlyph + 技能感知文案 + 呼吸光晕
// ═══════════════════════════════════════════════════════════════════════════

const SKILL_EMPTY_STATE: Record<SkillType, { title: string; subtitle: string }> = {
  bazi: {
    title: "探索你的命理",
    subtitle: "分享你的生辰，开启八字解读之旅",
  },
  zodiac: {
    title: "倾听星辰的声音",
    subtitle: "让星盘为你揭示宇宙的密语",
  },
  mbti: {
    title: "发现真实的自己",
    subtitle: "通过对话，探索你的人格特质",
  },
  attach: {
    title: "理解你的依恋模式",
    subtitle: "探索亲密关系中的自我",
  },
  career: {
    title: "规划你的职业蓝图",
    subtitle: "让我们一起探索你的职业发展方向",
  },
};

// Quick prompts for each skill
const SKILL_QUICK_PROMPTS: Record<SkillType, string[]> = {
  bazi: [
    "我是1990年5月15日早上8点出生的",
    "帮我分析今年的运势",
    "我的事业运如何？",
    "什么时候适合做重大决定？",
  ],
  zodiac: [
    "我是双子座，帮我看看本周运势",
    "我的太阳星座和上升星座有什么区别？",
    "双子座和什么星座最配？",
    "水逆对我有什么影响？",
  ],
  mbti: [
    "帮我测测我的MBTI类型",
    "INFP适合什么职业？",
    "我是INTJ，如何改善人际关系？",
    "E人和I人有什么本质区别？",
  ],
  attach: [
    "帮我分析我的依恋类型",
    "回避型依恋如何建立亲密关系？",
    "为什么我总是害怕被抛弃？",
    "如何治愈童年的情感创伤？",
  ],
  career: [
    "我该如何规划职业发展？",
    "我适合创业还是打工？",
    "如何在面试中展现自己？",
    "转行需要考虑什么？",
  ],
};

// 生成当日问候数据
function useDailyGreeting() {
  return useMemo(() => {
    const now = new Date();
    const hour = now.getHours();

    // 基于时间的问候语
    const greetings = {
      morning: "早安，愿你今日能量满满",
      afternoon: "午后时光，适合沉淀思考",
      evening: "晚间静谧，回顾今日收获",
    };

    let greeting = greetings.morning;
    if (hour >= 12 && hour < 18) greeting = greetings.afternoon;
    else if (hour >= 18) greeting = greetings.evening;

    // 节气（简化版，实际应根据日期计算）
    const solarTerms = ["小寒", "大寒", "立春", "雨水", "惊蛰", "春分"];
    const termIdx = Math.floor((now.getMonth() * 2 + Math.floor(now.getDate() / 15)) % 6);

    return {
      greeting,
      solarTerm: solarTerms[termIdx],
      termDescription: "万物生长",
      todayTip: "今日宜专注当下，一步一脚印",
    };
  }, []);
}

function ChatEmptyState({ skill, onQuickPrompt }: { skill: SkillType; onQuickPrompt?: (prompt: string) => void }) {
  const config = SKILL_EMPTY_STATE[skill] || SKILL_EMPTY_STATE.bazi;
  const quickPrompts = SKILL_QUICK_PROMPTS[skill] || SKILL_QUICK_PROMPTS.bazi;
  const dailyData = useDailyGreeting();

  return (
    <div className="flex flex-col items-center justify-center max-w-xl mx-auto px-4 py-6 w-full">
      {/* Daily Greeting 每日问候 */}
      <DailyGreeting
        greeting={dailyData.greeting}
        solarTerm={dailyData.solarTerm}
        termDescription={dailyData.termDescription}
        todayTip={dailyData.todayTip}
        className="mb-6 w-full"
      />

      {/* Animated glyph with enhanced aura */}
      <div className="relative flex items-center justify-center mb-4">
        {/* Outer pulsing ring */}
        <div className="absolute w-16 h-16 rounded-full border border-skill-primary/20 animate-pulse-slow" />
        <div className="absolute w-24 h-24 rounded-full border border-skill-primary/10 animate-pulse-slower" />

        {/* Breath aura */}
        <BreathAura
          skill={skill}
          size="sm"
          position="center"
          intensity="medium"
          className="opacity-40"
        />

        {/* Central glyph */}
        <VibeGlyph
          size="sm"
          skill={skill}
          showAura={true}
          animate={true}
          className="relative z-10"
        />
      </div>

      {/* Title */}
      <h3 className="text-base md:text-lg font-serif font-semibold text-foreground mb-1 text-center">
        {config.title}
      </h3>

      {/* Subtitle */}
      <p className="text-sm text-muted-foreground max-w-sm text-center mb-4">
        {config.subtitle}
      </p>

      {/* Quick prompt suggestions */}
      <div className="w-full space-y-2">
        <p className="text-xs text-muted-foreground/60 text-center mb-2">
          试试这样问
        </p>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
          {quickPrompts.slice(0, 4).map((prompt, index) => (
            <button
              key={index}
              onClick={() => onQuickPrompt?.(prompt)}
              className="group relative px-3 py-2 text-left text-sm text-muted-foreground
                       bg-card hover:bg-card/80 border border-border/50 hover:border-border
                       rounded-lg transition-all duration-200
                       hover:shadow-sm"
            >
              <span className="line-clamp-2">{prompt}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export function ChatContainer({
  skillId,
  skill = "bazi",
  conversationId,
  voiceMode = "warm",
  onConversationStart,
}: ChatContainerProps) {
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Map skillId to SkillType if not provided
  const activeSkill: SkillType = skill || (skillId as SkillType) || "bazi";

  // Use AI SDK powered chat hook
  const {
    messages,
    isLoading,
    sendMessage,
    sendQuickPrompt,
    error,
  } = useVibeChat({
    skillId: (skillId || skill) as SkillId,
    voiceMode,
    conversationId,
    onConversationStart,
    onError: (err) => console.error("Chat error:", err),
  });

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  // Handle send from ChatInput
  const handleSend = useCallback(
    async (content: string) => {
      await sendMessage(content);
    },
    [sendMessage]
  );

  // Handle quick prompt from empty state
  const handleQuickPrompt = useCallback(
    async (content: string) => {
      await sendQuickPrompt(content);
    },
    [sendQuickPrompt]
  );

  const hasMessages = messages.length > 0;

  return (
    <div className="flex flex-col h-full" data-skill={activeSkill}>
      {/* Messages area - always flex-1 to push input to bottom */}
      <div className="flex-1 overflow-y-auto relative min-h-0">
        {!hasMessages ? (
          /* Empty state - centered within scrollable area */
          <div className="flex flex-col items-center justify-end h-full pb-4 px-4">
            <ChatEmptyState skill={activeSkill} onQuickPrompt={handleQuickPrompt} />
          </div>
        ) : (
          /* Messages list - PC端左对齐，移动端居中 */
          <div className="px-4 pt-4 pb-2 max-w-3xl lg:ml-4 lg:mr-auto mx-auto">
            {messages.map((message) => (
              <div key={message.id}>
                <ChatMessage
                  role={message.role as "user" | "assistant"}
                  content={getMessageContent(message)}
                  isStreaming={isLoading && message.role === "assistant" && message === messages[messages.length - 1]}
                />
              </div>
            ))}
            {/* Show loading indicator when waiting for first response chunk */}
            {isLoading && messages.length > 0 && messages[messages.length - 1].role === "user" && (
              <ChatMessage
                role="assistant"
                content=""
                isStreaming
              />
            )}
            {/* Show error message */}
            {error && (
              <div className="text-red-500 text-sm text-center py-2">
                抱歉，出了点问题。请稍后再试。
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Input - fixed at bottom */}
      <div className="flex-shrink-0">
        <ChatInput onSend={handleSend} disabled={isLoading} />
      </div>
    </div>
  );
}

