/**
 * Next.js API Route for Chat - AI SDK 6 Compatible
 *
 * Implements Data Stream Protocol with x-vercel-ai-ui-message-stream header
 * Proxies to Python backend and converts SSE format
 */

import { NextRequest, NextResponse } from 'next/server';

// 服务端使用内部地址直接访问后端 API
// VIBELIFE_API_INTERNAL 是服务端环境变量（非 NEXT_PUBLIC_）
const API_BASE = process.env.VIBELIFE_API_INTERNAL
  ? `${process.env.VIBELIFE_API_INTERNAL}/api/v1`
  : "http://127.0.0.1:8000/api/v1";

export const runtime = 'edge';

/**
 * AI SDK 6 Data Stream Protocol format:
 * - text-start: {"type":"text-start","id":"[id]"}
 * - text-delta: {"type":"text-delta","id":"[id]","delta":"[content]"}
 * - text-end: {"type":"text-end","id":"[id]"}
 * - [DONE] to terminate
 */
export async function POST(req: NextRequest) {
  const body = await req.json();
  const { messages, skill, voice_mode, conversation_id } = body;

  // Extract the latest user message
  const lastMessage = messages?.[messages.length - 1];
  const userMessage = typeof lastMessage === 'string'
    ? lastMessage
    : lastMessage?.content || lastMessage?.parts?.[0]?.text || body.message || '';

  // Get auth token from header
  const authHeader = req.headers.get('authorization');

  // Prepare request to Python backend
  const chatRequest = {
    message: userMessage,
    skill: skill || 'bazi',
    voice_mode: voice_mode || 'warm',
    conversation_id: conversation_id,
  };

  try {
    // Call Python backend SSE endpoint
    const response = await fetch(`${API_BASE}/chat/stream`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        ...(authHeader && { Authorization: authHeader }),
      },
      body: JSON.stringify(chatRequest),
    });

    if (!response.ok) {
      const error = await response.json().catch(() => ({ detail: 'Chat failed' }));
      return NextResponse.json({ error: error.detail || 'Chat failed' }, { status: response.status });
    }

    // Create ReadableStream for AI SDK 6 Data Stream Protocol
    const encoder = new TextEncoder();
    const messageId = `msg-${Date.now()}`;

    const stream = new ReadableStream({
      async start(controller) {
        const reader = response.body?.getReader();
        const decoder = new TextDecoder();

        if (!reader) {
          controller.close();
          return;
        }

        // Send message-start
        controller.enqueue(encoder.encode(`data: {"type":"message-start","id":"${messageId}"}\n\n`));

        // Send text-start
        const textId = `text-${Date.now()}`;
        controller.enqueue(encoder.encode(`data: {"type":"text-start","id":"${textId}"}\n\n`));

        let buffer = '';

        try {
          while (true) {
            const { done, value } = await reader.read();
            if (done) break;

            buffer += decoder.decode(value, { stream: true });
            const lines = buffer.split('\n');
            buffer = lines.pop() || '';

            for (const line of lines) {
              if (line.startsWith('data: ')) {
                const dataStr = line.slice(6).trim();
                if (!dataStr) continue;

                try {
                  const data = JSON.parse(dataStr);

                  if (data.type === 'chunk') {
                    // Convert to AI SDK 6 text-delta format
                    const delta = JSON.stringify(data.content).slice(1, -1); // escape for JSON
                    controller.enqueue(encoder.encode(
                      `data: {"type":"text-delta","id":"${textId}","delta":"${delta}"}\n\n`
                    ));
                  } else if (data.type === 'done') {
                    // Send text-end
                    controller.enqueue(encoder.encode(`data: {"type":"text-end","id":"${textId}"}\n\n`));

                    // Send message-end with metadata
                    const metadata = {
                      conversation_id: data.conversation_id,
                      skill: data.skill,
                      voice_mode: data.voice_mode,
                    };
                    controller.enqueue(encoder.encode(
                      `data: {"type":"message-end","id":"${messageId}","metadata":${JSON.stringify(metadata)}}\n\n`
                    ));

                    // Terminate stream
                    controller.enqueue(encoder.encode('data: [DONE]\n\n'));
                  } else if (data.type === 'error') {
                    controller.enqueue(encoder.encode(
                      `data: {"type":"error","error":"${data.message}"}\n\n`
                    ));
                  }
                } catch {
                  // Skip malformed JSON
                }
              }
            }
          }
        } catch (error) {
          controller.enqueue(encoder.encode(
            `data: {"type":"error","error":"${error instanceof Error ? error.message : 'Unknown error'}"}\n\n`
          ));
        } finally {
          controller.close();
        }
      },
    });

    return new Response(stream, {
      headers: {
        'Content-Type': 'text/event-stream',
        'Cache-Control': 'no-cache',
        'Connection': 'keep-alive',
        'x-vercel-ai-ui-message-stream': 'v1',
      },
    });
  } catch (error) {
    return NextResponse.json(
      { error: error instanceof Error ? error.message : 'Unknown error' },
      { status: 500 }
    );
  }
}

"use client";

/**
 * Zodiac Landing Page - /zodiac
 * 星座占星首页
 */

import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import { ArrowRight, Sparkles } from "lucide-react";
import { LuminousPaper, VibeGlyph, BreathAura } from "@/components/core";

const SKILL_CONFIG = {
  title: "星座占星",
  subtitle: "倾听星辰的声音，发现内在潜能",
  description: "西方占星学与 AI 的完美结合，解读你的星盘，揭示宇宙对你的启示。",
  features: ["完整星盘分析", "行星运行解读", "流年运势预测", "星座关系匹配"],
};

export default function ZodiacLandingPage() {
  const router = useRouter();
  const handleStart = () => router.push("/zodiac/chat");

  return (
    <LuminousPaper skill="zodiac" variant="default" className="min-h-screen">
      <BreathAura skill="zodiac" size="xl" position="center" intensity="medium" className="opacity-40" />

      <div className="relative z-10 min-h-screen flex flex-col">
        <header className="py-6 px-6">
          <div className="flex items-center gap-2">
            <VibeGlyph size="sm" skill="zodiac" showAura={false} />
            <span className="font-serif font-semibold text-foreground">VibeLife</span>
          </div>
        </header>

        <main className="flex-1 flex items-center justify-center px-6 pb-20">
          <div className="max-w-2xl text-center">
            <motion.div initial={{ scale: 0.8, opacity: 0 }} animate={{ scale: 1, opacity: 1 }} transition={{ duration: 0.6 }} className="mb-8">
              <div className="relative inline-block">
                <BreathAura skill="zodiac" size="lg" position="center" intensity="high" className="opacity-60" />
                <VibeGlyph size="lg" skill="zodiac" showAura={true} animate={true} />
              </div>
            </motion.div>

            <motion.h1 initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: 0.2, duration: 0.6 }} className="font-serif text-4xl md:text-5xl font-bold text-foreground mb-4">{SKILL_CONFIG.title}</motion.h1>
            <motion.p initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: 0.3, duration: 0.6 }} className="text-xl text-muted-foreground mb-6">{SKILL_CONFIG.subtitle}</motion.p>
            <motion.p initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: 0.4, duration: 0.6 }} className="text-base text-muted-foreground/80 mb-8 max-w-lg mx-auto">{SKILL_CONFIG.description}</motion.p>

            <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: 0.5, duration: 0.6 }} className="flex flex-wrap justify-center gap-3 mb-10">
              {SKILL_CONFIG.features.map((feature, index) => (
                <span key={index} className="px-4 py-2 rounded-full bg-card border border-border text-sm text-muted-foreground">{feature}</span>
              ))}
            </motion.div>

            <motion.div initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ delay: 0.6, duration: 0.6 }}>
              <button onClick={handleStart} className="group inline-flex items-center gap-3 px-8 py-4 bg-skill-primary text-white rounded-xl font-medium text-lg shadow-lg hover:shadow-xl transition-all duration-300 hover:-translate-y-0.5">
                <Sparkles className="w-5 h-5" />开始对话<ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
              </button>
            </motion.div>

            <motion.p initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.8, duration: 0.6 }} className="mt-8 text-sm text-muted-foreground/60">每个人都值得被深度理解</motion.p>
          </div>
        </main>
      </div>
    </LuminousPaper>
  );
}

