"use client";

/**
 * MobileTabBar - 移动端底部导航栏
 * Based on: vibelife spec v3.0
 *
 * ┌────────┬────────┬────────┬────────┐
 * │  Chat  │  Chart │ Journey│   Me   │
 * │   💬   │   📊   │   🛤   │   👤   │
 * └────────┴────────┴────────┴────────┘
 */

import { cn } from "@/lib/utils";
import {
  MessageSquare,
  LayoutDashboard,
  Clock,
  User,
} from "lucide-react";
import { type TabType } from "./AppShell";

// ═══════════════════════════════════════════════════════════════════════════
// Types & Config
// ═══════════════════════════════════════════════════════════════════════════

interface MobileTabBarProps {
  activeTab: TabType;
  onTabChange: (tab: TabType) => void;
  className?: string;
}

interface TabItem {
  id: TabType;
  icon: typeof MessageSquare;
  label: string;
}

const TAB_ITEMS: TabItem[] = [
  { id: "chat", icon: MessageSquare, label: "对话" },
  { id: "chart", icon: LayoutDashboard, label: "图表" },
  { id: "journey", icon: Clock, label: "回顾" },
  { id: "me", icon: User, label: "我的" },
];

// ═══════════════════════════════════════════════════════════════════════════
// MobileTabBar Component
// ═══════════════════════════════════════════════════════════════════════════

export function MobileTabBar({ activeTab, onTabChange, className }: MobileTabBarProps) {
  return (
    <nav
      className={cn(
        "flex items-center justify-around",
        "h-16 bg-card border-t border-border",
        "safe-area-pb", // iOS safe area
        className
      )}
    >
      {TAB_ITEMS.map((item) => {
        const Icon = item.icon;
        const isActive = activeTab === item.id;

        return (
          <button
            key={item.id}
            onClick={() => onTabChange(item.id)}
            className={cn(
              "flex-1 flex flex-col items-center justify-center gap-1 py-2",
              "transition-colors duration-200",
              isActive
                ? "text-skill-primary"
                : "text-muted-foreground"
            )}
          >
            <Icon
              className={cn(
                "w-5 h-5 transition-transform duration-200",
                isActive && "scale-110"
              )}
            />
            <span
              className={cn(
                "text-xs font-medium",
                isActive && "font-semibold"
              )}
            >
              {item.label}
            </span>

            {/* Active indicator dot */}
            {isActive && (
              <span className="absolute bottom-1 w-1 h-1 rounded-full bg-skill-primary" />
            )}
          </button>
        );
      })}
    </nav>
  );
}

