import type { Metadata, Viewport } from "next";
import { cookies } from "next/headers";
import { Fraunces, Bricolage_Grotesque } from "next/font/google";
import { Providers } from "@/providers";
import { type SkillType } from "@/components/core";
import "./globals.css";

// ═══════════════════════════════════════════════════════════════════════════
// LUMINOUS PAPER Typography System
// ═══════════════════════════════════════════════════════════════════════════

// Display Serif - for emotional headlines, insight titles
const fraunces = Fraunces({
  subsets: ["latin"],
  variable: "--font-fraunces",
  display: "swap",
  weight: ["400", "500", "600", "700"],
  style: ["normal", "italic"],
});

// UI Sans - for body text, buttons, navigation
const bricolage = Bricolage_Grotesque({
  subsets: ["latin"],
  variable: "--font-bricolage",
  display: "swap",
  weight: ["400", "500", "600", "700"],
});

// ═══════════════════════════════════════════════════════════════════════════
// Metadata
// ═══════════════════════════════════════════════════════════════════════════

export const metadata: Metadata = {
  title: {
    default: "VibeLife - The Living Companion",
    template: "%s | VibeLife",
  },
  description: "每个人都值得被深度理解 - AI-powered personal growth platform combining Eastern wisdom and modern psychology",
  keywords: ["AI", "personal growth", "八字", "星座", "MBTI", "VibeLife", "命理", "占星", "人格测试"],
  authors: [{ name: "VibeLife" }],
  creator: "VibeLife",
  openGraph: {
    type: "website",
    locale: "zh_CN",
    url: "https://vibelife.app",
    siteName: "VibeLife",
    title: "VibeLife - The Living Companion",
    description: "每个人都值得被深度理解",
    images: [
      {
        url: "/og-image.png",
        width: 1200,
        height: 630,
        alt: "VibeLife",
      },
    ],
  },
  twitter: {
    card: "summary_large_image",
    title: "VibeLife - The Living Companion",
    description: "每个人都值得被深度理解",
  },
  icons: {
    icon: "/favicon.ico",
    apple: "/apple-touch-icon.png",
  },
};

export const viewport: Viewport = {
  width: "device-width",
  initialScale: 1,
  maximumScale: 1,
  themeColor: [
    { media: "(prefers-color-scheme: light)", color: "#FBF7F1" },
    { media: "(prefers-color-scheme: dark)", color: "#11110F" },
  ],
};

// ═══════════════════════════════════════════════════════════════════════════
// Root Layout
// ═══════════════════════════════════════════════════════════════════════════

export default async function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  // 从 cookie 获取 skill（由 middleware 设置）
  const cookieStore = await cookies();
  const skillCookie = cookieStore.get("vibelife-skill");
  const initialSkill = (skillCookie?.value as SkillType) || "bazi";

  return (
    <html
      lang="zh-CN"
      className={`${fraunces.variable} ${bricolage.variable}`}
      suppressHydrationWarning
    >
      <body className="font-sans antialiased">
        <Providers initialSkill={initialSkill}>
          {children}
        </Providers>
      </body>
    </html>
  );
}

