"use client";

/**
 * Root Page - 根据 skill 上下文显示对应页面
 *
 * 路由逻辑:
 * - 无子域名访问 vibelife.app -> 显示品牌首页（Skill 选择器）
 * - 有子域名或已设置 skill -> 重定向到对应 skill 的 landing page
 */

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { useSkill } from "@/providers";
import { motion } from "framer-motion";
import { ArrowRight, Compass, Star, Brain, Heart, Briefcase } from "lucide-react";
import { LuminousPaper, VibeGlyph, BreathAura, type SkillType } from "@/components/core";

// ═══════════════════════════════════════════════════════════════════════════
// Skill Cards Configuration
// ═══════════════════════════════════════════════════════════════════════════

const SKILLS: Array<{
  id: SkillType;
  name: string;
  subtitle: string;
  description: string;
  icon: typeof Compass;
  available: boolean;
  gradient: string;
}> = [
  {
    id: "bazi",
    name: "八字命理",
    subtitle: "解读天赋，洞察人生",
    description: "基于出生时间的八字分析，了解你的性格特质、天赋才能和人生机遇",
    icon: Compass,
    available: true,
    gradient: "from-amber-500 to-orange-600",
  },
  {
    id: "zodiac",
    name: "星座占星",
    subtitle: "星辰指引，探索潜能",
    description: "通过星盘解读，发现你的内在动力、情感模式和成长方向",
    icon: Star,
    available: true,
    gradient: "from-purple-500 to-pink-600",
  },
  {
    id: "mbti",
    name: "MBTI 人格",
    subtitle: "发现真我，理解特质",
    description: "科学的人格测评，帮你认识自己的思维方式和行为模式",
    icon: Brain,
    available: false,
    gradient: "from-blue-500 to-cyan-600",
  },
  {
    id: "attach",
    name: "依恋模式",
    subtitle: "理解依恋，改善关系",
    description: "探索你的依恋风格，建立更健康的亲密关系",
    icon: Heart,
    available: false,
    gradient: "from-rose-500 to-red-600",
  },
  {
    id: "career",
    name: "职业发展",
    subtitle: "规划蓝图，找到方向",
    description: "结合你的特质和兴趣，规划适合的职业发展路径",
    icon: Briefcase,
    available: false,
    gradient: "from-emerald-500 to-teal-600",
  },
];

// ═══════════════════════════════════════════════════════════════════════════
// Root Page Component
// ═══════════════════════════════════════════════════════════════════════════

export default function RootPage() {
  const router = useRouter();
  const { skill } = useSkill();
  const [showBrandIndex, setShowBrandIndex] = useState(false);
  const [isChecking, setIsChecking] = useState(true);

  useEffect(() => {
    // 检查是否有子域名或者已经选择了 skill
    const hostname = typeof window !== "undefined" ? window.location.hostname : "";
    const parts = hostname.split(".");

    // 检测子域名
    const hasSubdomain =
      parts.length >= 2 &&
      ["bazi", "zodiac", "mbti", "attach", "career"].includes(parts[0].toLowerCase());

    if (hasSubdomain) {
      // 有子域名，重定向到对应的 skill 首页
      const subdomain = parts[0].toLowerCase() as SkillType;
      router.replace(`/${subdomain}`);
    } else {
      // 无子域名，显示品牌首页
      setShowBrandIndex(true);
    }
    setIsChecking(false);
  }, [router]);

  const handleSkillClick = (selectedSkill: typeof SKILLS[0]) => {
    if (!selectedSkill.available) return;

    // Set cookie and navigate
    document.cookie = `vibelife-skill=${selectedSkill.id}; path=/; max-age=${60 * 60 * 24 * 365}`;
    router.push(`/${selectedSkill.id}`);
  };

  // Loading state
  if (isChecking) {
    return (
      <LuminousPaper skill={skill} variant="default" className="min-h-screen">
        <div className="flex items-center justify-center min-h-screen">
          <VibeGlyph size="md" skill={skill} showAura={true} animate={true} />
        </div>
      </LuminousPaper>
    );
  }

  // 品牌首页 - Skill 选择器
  if (showBrandIndex) {
    return (
      <LuminousPaper skill="bazi" variant="default" className="min-h-screen">
        {/* Background */}
        <BreathAura
          skill="bazi"
          size="xl"
          position="center"
          intensity="low"
          className="opacity-30"
        />

        <div className="relative z-10 min-h-screen flex flex-col">
          {/* Header */}
          <header className="py-6 px-6">
            <div className="flex items-center gap-2">
              <VibeGlyph size="sm" skill="bazi" showAura={false} />
              <span className="font-serif font-semibold text-foreground">VibeLife</span>
            </div>
          </header>

          {/* Main Content */}
          <main className="flex-1 flex items-center justify-center px-6 pb-20">
            <div className="max-w-4xl w-full">
              {/* Title */}
              <motion.div
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ duration: 0.6 }}
                className="text-center mb-12"
              >
                <h1 className="font-serif text-4xl md:text-5xl font-bold text-foreground mb-4">
                  每个人都值得被深度理解
                </h1>
                <p className="text-xl text-muted-foreground max-w-lg mx-auto">
                  东方智慧 × AI 科技，开启自我探索之旅
                </p>
              </motion.div>

              {/* Skill Cards Grid */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {SKILLS.map((skillItem, index) => (
                  <motion.div
                    key={skillItem.id}
                    initial={{ y: 20, opacity: 0 }}
                    animate={{ y: 0, opacity: 1 }}
                    transition={{ delay: 0.1 * (index + 1), duration: 0.5 }}
                  >
                    <button
                      onClick={() => handleSkillClick(skillItem)}
                      disabled={!skillItem.available}
                      className={`
                        w-full p-6 rounded-2xl border text-left transition-all duration-300
                        ${skillItem.available
                          ? "bg-card hover:bg-card/80 border-border hover:border-skill-primary/50 hover:shadow-lg hover:-translate-y-1 cursor-pointer"
                          : "bg-muted/50 border-border/50 cursor-not-allowed opacity-60"
                        }
                      `}
                    >
                      <div className="flex items-start justify-between mb-4">
                        <div className={`
                          w-12 h-12 rounded-xl flex items-center justify-center
                          ${skillItem.available ? "bg-skill-primary/10 text-skill-primary" : "bg-muted text-muted-foreground"}
                        `}>
                          <skillItem.icon className="w-6 h-6" />
                        </div>
                        {skillItem.available && (
                          <ArrowRight className="w-5 h-5 text-muted-foreground" />
                        )}
                        {!skillItem.available && (
                          <span className="text-xs text-muted-foreground bg-muted px-2 py-1 rounded">
                            即将推出
                          </span>
                        )}
                      </div>
                      <h3 className="font-serif text-xl font-semibold text-foreground mb-2">
                        {skillItem.name}
                      </h3>
                      <p className="text-sm text-muted-foreground">
                        {skillItem.subtitle}
                      </p>
                    </button>
                  </motion.div>
                ))}
              </div>

              {/* Tagline */}
              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.8, duration: 0.6 }}
                className="mt-12 text-center text-sm text-muted-foreground/60"
              >
                The Living Companion
              </motion.p>
            </div>
          </main>
        </div>
      </LuminousPaper>
    );
  }

  // Fallback - should not reach here
  return null;
}

file.content:
"use client";

import { motion } from "framer-motion";
import { cn } from "@/lib/utils";

/**
 * DailyGreeting - 每日问候
 *
 * 用途: Chat 页面顶部强制展示的每日问候
 * 特点: 节气/天象感知，今日一步 checkbox
 */

interface DailyGreetingProps {
  solarTerm?: string; // 节气名称
  termDescription?: string; // 节气描述
  greeting: string; // 主问候语
  todayTip?: string; // 今日提示
  actionItem?: {
    text: string; // 今日一步文本
    completed?: boolean;
  };
  onActionToggle?: () => void;
  className?: string;
}

export function DailyGreeting({
  solarTerm,
  termDescription,
  greeting,
  todayTip,
  actionItem,
  onActionToggle,
  className,
}: DailyGreetingProps) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "glass-card rounded-xl p-5 border-l-4 border-l-antique-gold",
        "bg-gradient-to-r from-card to-card/80",
        className
      )}
    >
      {/* 节气标题 */}
      {solarTerm && (
        <div className="flex items-center gap-2 mb-3">
          <span className="text-lg">🎋</span>
          <span className="font-serif font-semibold text-foreground">
            {solarTerm}
          </span>
          {termDescription && (
            <span className="text-muted-foreground text-sm">
              · {termDescription}
            </span>
          )}
        </div>
      )}

      {/* 分隔线 */}
      {solarTerm && <div className="h-px bg-border/50 mb-4" />}

      {/* 问候语 - 使用墨水显影动画 */}
      <p className="text-foreground leading-relaxed mb-4 ink-text">{greeting}</p>

      {/* 今日提示 */}
      {todayTip && (
        <p className="text-sm text-muted-foreground mb-4 ink-text-delay-1">
          {todayTip}
        </p>
      )}

      {/* 今日一步 */}
      {actionItem && (
        <div className="bg-muted/30 rounded-lg p-4 border border-border/30">
          <p className="text-xs text-muted-foreground mb-2 font-medium">
            今日一步
          </p>
          <button
            onClick={onActionToggle}
            className="flex items-center gap-3 text-foreground group w-full text-left"
          >
            <span
              className={cn(
                "w-5 h-5 rounded border-2 flex items-center justify-center",
                "transition-all duration-200",
                actionItem.completed
                  ? "bg-antique-gold border-antique-gold text-white"
                  : "border-border group-hover:border-antique-gold/50"
              )}
            >
              {actionItem.completed && (
                <svg
                  className="w-3 h-3"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={3}
                    d="M5 13l4 4L19 7"
                  />
                </svg>
              )}
            </span>
            <span
              className={cn(
                "flex-1 transition-colors",
                actionItem.completed && "line-through text-muted-foreground"
              )}
            >
              {actionItem.text}
            </span>
          </button>
        </div>
      )}
    </motion.div>
  );
}

// 紧凑版本
export function DailyGreetingCompact({
  greeting,
  solarTerm,
  className,
}: Pick<DailyGreetingProps, "greeting" | "solarTerm" | "className">) {
  return (
    <div
      className={cn(
