          <div className="flex items-start gap-3">
            <div className="w-8 h-8 rounded-lg bg-purple-500/10 flex items-center justify-center flex-shrink-0">
              <span className="text-sm">☯️</span>
            </div>
            <div className="flex-1 min-w-0">
              <p className="text-xs text-muted-foreground/80 mb-0.5">命理提示</p>
              <p className="text-sm text-foreground">
                {baziHint}
              </p>
            </div>
          </div>
        )}

        {/* 今日一步 */}
        {actionItem && (
          <div className="pt-2 border-t border-border/30">
            <button
              onClick={onActionToggle}
              className="flex items-center gap-3 text-foreground group w-full text-left p-2 -m-2 rounded-lg hover:bg-muted/30 transition-colors"
            >
              <span
                className={cn(
                  "w-5 h-5 rounded border-2 flex items-center justify-center flex-shrink-0",
                  "transition-all duration-200",
