// 生成当日问候数据
function useDailyGreeting(skill: SkillType) {
  return useMemo(() => {
    const now = new Date();
    const hour = now.getHours();
    const month = now.getMonth();
    const day = now.getDate();

    // 确定时段
    type TimeOfDay = "dawn" | "morning" | "afternoon" | "evening" | "night";
    let timeOfDay: TimeOfDay = "morning";
    let greeting = "新的一天开始了";

    if (hour >= 5 && hour < 7) {
      timeOfDay = "dawn";
      greeting = "晨光熹微";
    } else if (hour >= 7 && hour < 12) {
      timeOfDay = "morning";
      greeting = "愿你能量满满";
    } else if (hour >= 12 && hour < 18) {
      timeOfDay = "afternoon";
      greeting = "保持专注";
    } else if (hour >= 18 && hour < 21) {
      timeOfDay = "evening";
      greeting = "辛苦一天了";
    } else {
      timeOfDay = "night";
      greeting = "夜深了，早点休息";
    }

    // 基于日期计算节气（简化算法）
    const solarTerms = [
      "小寒", "大寒", // 1月
      "立春", "雨水", // 2月
      "惊蛰", "春分", // 3月
      "清明", "谷雨", // 4月
      "立夏", "小满", // 5月
      "芒种", "夏至", // 6月
      "小暑", "大暑", // 7月
      "立秋", "处暑", // 8月
      "白露", "秋分", // 9月
      "寒露", "霜降", // 10月
      "立冬", "小雪", // 11月
      "大雪", "冬至", // 12月
    ];
    const termIdx = month * 2 + (day >= 15 ? 1 : 0);
    const solarTerm = solarTerms[termIdx % 24];

    // 基于节气的建议
    const seasonTips: Record<string, string> = {
      小寒: "冬藏时节，利于回顾与规划",
      大寒: "严寒之际，蓄力待发",
      立春: "万物复苏，适合启动新计划",
      雨水: "润物无声，静心滋养",
      惊蛰: "春雷惊醒，行动起来",
      春分: "昼夜平分，调和身心",
      清明: "天清地明，整理思绪",
      谷雨: "雨生百谷，播种希望",
      立夏: "夏气渐盛，保持活力",
      小满: "小有所成，继续努力",
      芒种: "播种收获，勤勉不懈",
      夏至: "阳气鼎盛，适度休息",
      小暑: "暑气初升，清心宁神",
      大暑: "酷暑难耐，静以养心",
      立秋: "秋意渐起，收获时节",
      处暑: "暑气渐消，调整节奏",
      白露: "秋凉渐浓，收敛心神",
      秋分: "秋高气爽，平衡工作与生活",
      寒露: "露水渐寒，注意保养",
      霜降: "霜降水泽，沉淀积累",
      立冬: "冬季来临，储备能量",
      小雪: "雪花初降，内省时刻",
      大雪: "瑞雪兆丰，静待来年",
      冬至: "一阳初生，否极泰来",
    };

    // 基于技能的八字提示
    const baziHints: Record<string, string> = {
      小寒: "水旺之季，利于思考与规划",
      大寒: "水气最盛，适合内省沉淀",
      立春: "木气初生，宜开启新事业",
      春分: "木气旺盛，适合拓展人脉",
      立夏: "火气渐升，利于表达展现",
      夏至: "火气正盛，注意平衡",
      立秋: "金气初生，适合决断取舍",
      秋分: "金气旺盛，利于收获成果",
      立冬: "水气初生，宜休养生息",
      冬至: "一阳复始，厚积薄发",
    };

    // 格式化日期
    const dateStr = `${month + 1}月${day}日`;

    return {
      greeting,
      solarTerm,
      timeOfDay,
      date: dateStr,
      todayTip: seasonTips[solarTerm] || "专注当下，稳步前行",
      baziHint: skill === "bazi" ? (baziHints[solarTerm] || null) : null,
    };
  }, [skill]);
}

