}: ChatContainerProps) {
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Map skillId to SkillType if not provided
  const activeSkill: SkillType = skill || (skillId as SkillType) || "bazi";

  // Use AI SDK powered chat hook
  const {
    messages,
    isLoading,
    sendMessage,
    sendQuickPrompt,
    error,
  } = useVibeChat({
