// 生成当日问候数据
function useDailyGreeting(skill: SkillType) {
  return useMemo(() => {
    const now = new Date();
    const hour = now.getHours();
    const month = now.getMonth();
    const day = now.getDate();

    // 确定时段
    type TimeOfDay = "dawn" | "morning" | "afternoon" | "evening" | "night";
    let timeOfDay: TimeOfDay = "morning";
    let greeting = "新的一天开始了";

    if (hour >= 5 && hour < 7) {
      timeOfDay = "dawn";
      greeting = "晨光熹微";
    } else if (hour >= 7 && hour < 12) {
      timeOfDay = "morning";
      greeting = "愿你能量满满";
    } else if (hour >= 12 && hour < 18) {
      timeOfDay = "afternoon";
      greeting = "保持专注";
    } else if (hour >= 18 && hour < 21) {
      timeOfDay = "evening";
      greeting = "辛苦一天了";
    } else {
      timeOfDay = "night";
      greeting = "夜深了，早点休息";
    }

    // 基于日期计算节气（简化算法）
    const solarTerms = [
      "小寒", "大寒", // 1月
      "立春", "雨水", // 2月
      "惊蛰", "春分", // 3月
      "清明", "谷雨", // 4月
      "立夏", "小满", // 5月
