      立夏: "夏气渐盛，保持活力",
      小满: "小有所成，继续努力",
      芒种: "播种收获，勤勉不懈",
      夏至: "阳气鼎盛，适度休息",
      小暑: "暑气初升，清心宁神",
      大暑: "酷暑难耐，静以养心",
      立秋: "秋意渐起，收获时节",
      处暑: "暑气渐消，调整节奏",
      白露: "秋凉渐浓，收敛心神",
      秋分: "秋高气爽，平衡工作与生活",
      寒露: "露水渐寒，注意保养",
      霜降: "霜降水泽，沉淀积累",
      立冬: "冬季来临，储备能量",
      小雪: "雪花初降，内省时刻",
      大雪: "瑞雪兆丰，静待来年",
      冬至: "一阳初生，否极泰来",
    };

    // 基于技能的八字提示
    const baziHints: Record<string, string> = {
      小寒: "水旺之季，利于思考与规划",
      大寒: "水气最盛，适合内省沉淀",
      立春: "木气初生，宜开启新事业",
      春分: "木气旺盛，适合拓展人脉",
      立夏: "火气渐升，利于表达展现",
      夏至: "火气正盛，注意平衡",
      立秋: "金气初生，适合决断取舍",
