"use client";

import { useState, KeyboardEvent, useRef, useEffect } from "react";
import { cn } from "@/lib/utils";
import { Send, Mic, Paperclip } from "lucide-react";

// ═══════════════════════════════════════════════════════════════════════════
// ChatInput - LUMINOUS PAPER Design System
// Omnibar 玻璃胶囊样式：frosted glass 输入框
// ═══════════════════════════════════════════════════════════════════════════

export interface ChatInputProps {
  onSend: (message: string) => void;
  onFileSelect?: (file: File) => void;
  disabled?: boolean;
  placeholder?: string;
  showMediaButtons?: boolean;
  className?: string;
}

export function ChatInput({
  onSend,
  onFileSelect,
  disabled,
  placeholder = "有什么想问的...",
  showMediaButtons = true,
  className,
}: ChatInputProps) {
  const [message, setMessage] = useState("");
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Auto-resize textarea
  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${Math.min(textarea.scrollHeight, 120)}px`;
    }
  }, [message]);

  const handleSend = () => {
    if (message.trim() && !disabled) {
      onSend(message.trim());
      setMessage("");
      // Reset height
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const canSend = message.trim().length > 0 && !disabled;

  const handleFileClick = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file && onFileSelect) {
      onFileSelect(file);
    }
    // Reset input
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  return (
    <div className={cn("p-4 md:px-6 pb-6", className)}>
      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        accept="image/*,.pdf,.txt,.docx"
        onChange={handleFileChange}
        className="hidden"
        disabled={disabled}
      />

      {/* Gradient fade above input */}
      <div className="h-8 bg-gradient-to-t from-background to-transparent -mt-8 mb-4 pointer-events-none" />

      {/* PC端左对齐，移动端居中，最大宽度限制 */}
      <div className="max-w-3xl lg:ml-0 lg:mr-auto mx-auto">
        <div className="omnibar shadow-lg shadow-black/5">
          {/* Media buttons (optional) */}
          {showMediaButtons && (
            <>
              <button
                type="button"
                onClick={handleFileClick}
                className="omnibar-btn"
                aria-label="上传文件"
                disabled={disabled}
              >
                <Paperclip className="w-5 h-5" />
              </button>
              <button
                type="button"
                className="omnibar-btn"
                aria-label="语音输入"
                disabled={disabled}
              >
                <Mic className="w-5 h-5" />
              </button>
            </>
          )}

          {/* Input */}
          <textarea
            ref={textareaRef}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder={placeholder}
            disabled={disabled}
            rows={1}
            className="omnibar-input"
            style={{ minHeight: "24px", maxHeight: "120px" }}
          />

          {/* Send button with animation */}
          <button
            type="button"
            onClick={handleSend}
            disabled={!canSend}
            className={cn(
              "omnibar-send transition-all duration-300",
              canSend
                ? "scale-100 opacity-100"
                : "scale-90 opacity-50 cursor-not-allowed"
            )}
            aria-label="发送消息"
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        {/* Hint text */}
        <p className="text-center text-xs text-muted-foreground/60 mt-3">
          按 Enter 发送，Shift+Enter 换行
        </p>
      </div>
    </div>
  );
}

