// 生成当日问候数据
function useDailyGreeting(skill: SkillType) {
  return useMemo(() => {
    const now = new Date();
    const hour = now.getHours();
    const month = now.getMonth();
    const day = now.getDate();

    // 确定时段
    type TimeOfDay = "dawn" | "morning" | "afternoon" | "evening" | "night";
    let timeOfDay: TimeOfDay = "morning";
    let greeting = "新的一天开始了";

    if (hour >= 5 && hour < 7) {
      timeOfDay = "dawn";
      greeting = "晨光熹微";
    } else if (hour >= 7 && hour < 12) {
      timeOfDay = "morning";
      greeting = "愿你能量满满";
    } else if (hour >= 12 && hour < 18) {
      timeOfDay = "afternoon";
      greeting = "保持专注";
    } else if (hour >= 18 && hour < 21) {
      timeOfDay = "evening";
      greeting = "辛苦一天了";
    } else {
      timeOfDay = "night";
      greeting = "夜深了，早点休息";
    }

    // 基于日期计算节气（简化算法）
    const solarTerms = [
      "小寒", "大寒", // 1月
      "立春", "雨水", // 2月
      "惊蛰", "春分", // 3月
      "清明", "谷雨", // 4月
      "立夏", "小满", // 5月
      "芒种", "夏至", // 6月
      "小暑", "大暑", // 7月
      "立秋", "处暑", // 8月
      "白露", "秋分", // 9月
      "寒露", "霜降", // 10月
      "立冬", "小雪", // 11月
      "大雪", "冬至", // 12月
    ];
    const termIdx = month * 2 + (day >= 15 ? 1 : 0);
    const solarTerm = solarTerms[termIdx % 24];

    // 基于节气的建议
    const seasonTips: Record<string, string> = {
      小寒: "冬藏时节，利于回顾与规划",
      大寒: "严寒之际，蓄力待发",
      立春: "万物复苏，适合启动新计划",
      雨水: "润物无声，静心滋养",
      惊蛰: "春雷惊醒，行动起来",
      春分: "昼夜平分，调和身心",
      清明: "天清地明，整理思绪",
      谷雨: "雨生百谷，播种希望",
      立夏: "夏气渐盛，保持活力",
      小满: "小有所成，继续努力",
      芒种: "播种收获，勤勉不懈",
      夏至: "阳气鼎盛，适度休息",
      小暑: "暑气初升，清心宁神",
      大暑: "酷暑难耐，静以养心",
      立秋: "秋意渐起，收获时节",
      处暑: "暑气渐消，调整节奏",
      白露: "秋凉渐浓，收敛心神",
      秋分: "秋高气爽，平衡工作与生活",
      寒露: "露水渐寒，注意保养",
      霜降: "霜降水泽，沉淀积累",
      立冬: "冬季来临，储备能量",
      小雪: "雪花初降，内省时刻",
      大雪: "瑞雪兆丰，静待来年",
      冬至: "一阳初生，否极泰来",
    };

    // 基于技能的八字提示
    const baziHints: Record<string, string> = {
      小寒: "水旺之季，利于思考与规划",
      大寒: "水气最盛，适合内省沉淀",
      立春: "木气初生，宜开启新事业",
      春分: "木气旺盛，适合拓展人脉",
      立夏: "火气渐升，利于表达展现",
      夏至: "火气正盛，注意平衡",
      立秋: "金气初生，适合决断取舍",
      秋分: "金气旺盛，利于收获成果",
      立冬: "水气初生，宜休养生息",
      冬至: "一阳复始，厚积薄发",
    };

    // 格式化日期
    const dateStr = `${month + 1}月${day}日`;

    return {
      greeting,
      solarTerm,
      timeOfDay,
      date: dateStr,
      todayTip: seasonTips[solarTerm] || "专注当下，稳步前行",
      baziHint: skill === "bazi" ? (baziHints[solarTerm] || null) : null,
    };
  }, [skill]);
}

function ChatEmptyState({ skill, onQuickPrompt }: { skill: SkillType; onQuickPrompt?: (prompt: string) => void }) {
  const config = SKILL_EMPTY_STATE[skill] || SKILL_EMPTY_STATE.bazi;
  const quickPrompts = SKILL_QUICK_PROMPTS[skill] || SKILL_QUICK_PROMPTS.bazi;
  const dailyData = useDailyGreeting(skill);

  return (
    <div className="flex flex-col items-center justify-center max-w-xl mx-auto px-4 py-6 w-full">
      {/* Daily Greeting 每日问候 */}
      <DailyGreeting
        greeting={dailyData.greeting}
        solarTerm={dailyData.solarTerm}
        timeOfDay={dailyData.timeOfDay}
        date={dailyData.date}
        todayTip={dailyData.todayTip}
        baziHint={dailyData.baziHint || undefined}
        className="mb-6 w-full"
      />

      {/* Animated glyph with enhanced aura */}
      <div className="relative flex items-center justify-center mb-4">
        {/* Outer pulsing ring */}
        <div className="absolute w-16 h-16 rounded-full border border-skill-primary/20 animate-pulse-slow" />
        <div className="absolute w-24 h-24 rounded-full border border-skill-primary/10 animate-pulse-slower" />

        {/* Breath aura */}
        <BreathAura
          skill={skill}
          size="sm"
          position="center"
          intensity="medium"
          className="opacity-40"
        />

        {/* Central glyph */}
        <VibeGlyph
          size="sm"
          skill={skill}
          showAura={true}
          animate={true}
          className="relative z-10"
        />
      </div>

      {/* Title */}
      <h3 className="text-base md:text-lg font-serif font-semibold text-foreground mb-1 text-center">
        {config.title}
      </h3>

      {/* Subtitle */}
      <p className="text-sm text-muted-foreground max-w-sm text-center mb-4">
        {config.subtitle}
      </p>

      {/* Quick prompt suggestions */}
      <div className="w-full space-y-3">
        <p className="text-xs text-muted-foreground/60 text-center">
          试试这样问
        </p>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
          {quickPrompts.slice(0, 4).map((prompt, index) => (
            <button
              key={index}
              onClick={() => onQuickPrompt?.(prompt.question)}
              className={`
                group relative px-3 py-3 text-left
                bg-gradient-to-br ${prompt.gradient}
                border border-border/50 hover:border-skill-primary/30
                rounded-xl transition-all duration-200
                hover:shadow-md hover:-translate-y-0.5
              `}
            >
              <div className="flex items-start gap-2.5">
                <span className="text-lg flex-shrink-0">{prompt.icon}</span>
                <div className="flex-1 min-w-0">
                  <span className="text-[10px] font-medium text-muted-foreground/70 uppercase tracking-wide">
                    {prompt.category}
                  </span>
                  <p className="text-sm text-foreground line-clamp-2 mt-0.5">
                    {prompt.question}
                  </p>
                </div>
              </div>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export function ChatContainer({
  skillId,
  skill = "bazi",
  conversationId,
  voiceMode = "warm",
  onConversationStart,
}: ChatContainerProps) {
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Map skillId to SkillType if not provided
  const activeSkill: SkillType = skill || (skillId as SkillType) || "bazi";

  // Use AI SDK powered chat hook
  const {
    messages,
    isLoading,
    sendMessage,
    sendQuickPrompt,
    error,
  } = useVibeChat({
    skillId: (skillId || skill) as SkillId,
    voiceMode,
    conversationId,
    onConversationStart,
    onError: (err) => console.error("Chat error:", err),
  });

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  // Handle send from ChatInput
  const handleSend = useCallback(
    async (content: string) => {
      await sendMessage(content);
    },
    [sendMessage]
  );

  // Handle quick prompt from empty state
  const handleQuickPrompt = useCallback(
    async (content: string) => {
      await sendQuickPrompt(content);
    },
    [sendQuickPrompt]
  );

  const hasMessages = messages.length > 0;

  return (
    <div className="flex flex-col h-full" data-skill={activeSkill}>
      {/* Messages area - always flex-1 to push input to bottom */}
      <div className="flex-1 overflow-y-auto relative min-h-0">
        {!hasMessages ? (
          /* Empty state - centered within scrollable area */
          <div className="flex flex-col items-center justify-end h-full pb-4 px-4">
            <ChatEmptyState skill={activeSkill} onQuickPrompt={handleQuickPrompt} />
          </div>
        ) : (
          /* Messages list - PC端左对齐，移动端居中 */
          <div className="px-4 pt-4 pb-2 max-w-3xl lg:ml-4 lg:mr-auto mx-auto">
            {messages.map((message) => (
              <div key={message.id}>
                <ChatMessage
                  role={message.role as "user" | "assistant"}
                  content={getMessageContent(message)}
                  isStreaming={isLoading && message.role === "assistant" && message === messages[messages.length - 1]}
                />
              </div>
            ))}
            {/* Show loading indicator when waiting for first response chunk */}
            {isLoading && messages.length > 0 && messages[messages.length - 1].role === "user" && (
              <ChatMessage
                role="assistant"
                content=""
                isStreaming
              />
            )}
            {/* Show error message */}
            {error && (
              <div className="text-red-500 text-sm text-center py-2">
                抱歉，出了点问题。请稍后再试。
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Input - fixed at bottom */}
      <div className="flex-shrink-0">
        <ChatInput onSend={handleSend} disabled={isLoading} />
      </div>
    </div>
  );
}

