  // Create headers
  const headers = useMemo(() => {
    const h: Record<string, string> = { 'Content-Type': 'application/json' };
    if (accessToken) {
      h['Authorization'] = `Bearer ${accessToken}`;
    }
    return h;
  }, [accessToken]);

  // AI SDK 6 useChat hook - use simple api option
  const chat = useChat({
    api: '/api/chat',
    headers,
    body: {
      skill: skillId,
      voice_mode: voiceMode,
      conversation_id: conversationId,
    },
    onFinish: (message) => {
      // Extract conversation_id from metadata if available
      // @ts-expect-error - metadata is available but not typed
      const metadata = message?.metadata;
      if (metadata?.conversation_id && onConversationStart) {
        onConversationStart(metadata.conversation_id);
      }
      onFinish?.();
    },
    onError: (error) => {
      console.error('Chat error:', error);
      onError?.(error);
    },
  });

  // Handle send - useChat provides handleSubmit for form submission
  // and append for programmatic message addition
  const sendVibeMessage = useCallback(
    async (content: string) => {
      // Use append to add user message and trigger API call
      await chat.append({
        role: 'user',
        content,
      });
    },
    [chat]
  );

  // Clear messages and send (for quick prompts)
  const sendQuickPrompt = useCallback(
    async (content: string) => {
      chat.setMessages([]);
      return sendVibeMessage(content);
    },
    [chat, sendVibeMessage]
  );

  // Compute isLoading from isLoading prop
  const isLoading = chat.isLoading;

  return {
    // Core state
    messages: chat.messages,
    status: chat.isLoading ? 'streaming' : 'idle',
    isLoading,
    error: chat.error,

    // Actions
    sendMessage: sendVibeMessage,
    sendQuickPrompt,
    stop: chat.stop,
    clearError: () => {},

    // For advanced use
    setMessages: chat.setMessages,
    regenerate: chat.reload,

    // Metadata
    conversationId,
    skillId,
    voiceMode,
  };
}

export default useVibeChat;
