     1→import { NextResponse } from "next/server";
     2→import type { NextRequest } from "next/server";
     3→
     4→/**
     5→ * Middleware - 子域名路由 + Skill 检测
     6→ *
     7→ * 路由规则:
     8→ * - vibelife.app (无子域名) -> 品牌首页 (www 路由组)
     9→ * - bazi.vibelife.app -> /bazi/* 路由组
    10→ * - zodiac.vibelife.app -> /zodiac/* 路由组
    11→ *
    12→ * URL 重写示例:
    13→ * - bazi.vibelife.app/ -> /bazi
    14→ * - bazi.vibelife.app/chat -> /bazi/chat
    15→ * - zodiac.vibelife.app/chat -> /zodiac/chat
    16→ *
    17→ * 本地开发:
    18→ * - localhost:3000 -> 默认显示品牌首页
    19→ * - localhost:3000/bazi -> bazi 路由
    20→ * - localhost:3000/zodiac -> zodiac 路由
    21→ */
    22→
    23→// 支持的 skill 类型
    24→const VALID_SKILLS = ["bazi", "zodiac", "mbti", "attach", "career", "lifecoach"] as const;
    25→type ValidSkill = (typeof VALID_SKILLS)[number];
    26→
    27→// 当前已启用的 skills (有对应路由组)
    28→const ENABLED_SKILLS: ValidSkill[] = ["bazi", "zodiac", "lifecoach"];
    29→
    30→// 默认 skill
    31→const DEFAULT_SKILL: ValidSkill = "bazi";
    32→
    33→// 共享路由（不需要 skill 前缀）
    34→const SHARED_ROUTES = ["/auth", "/payment", "/checkout", "/interview", "/settings"];
    35→
    36→// 检查是否是共享路由
    37→function isSharedRoute(pathname: string): boolean {
    38→  return SHARED_ROUTES.some((route) => pathname.startsWith(route));
    39→}
    40→
    41→// 检查路径是否已经包含 skill 前缀
    42→function hasSkillPrefix(pathname: string): boolean {
    43→  const firstSegment = pathname.split("/")[1];
    44→  return VALID_SKILLS.includes(firstSegment as ValidSkill);
    45→}
    46→
    47→export default function middleware(request: NextRequest) {
    48→  const url = request.nextUrl.clone();
    49→  const hostname = request.headers.get("host") || "";
    50→  const pathname = url.pathname;
    51→
    52→  // 根路径直接重定向到 /chat
    53→  if (pathname === "/") {
    54→    url.pathname = "/chat";
    55→    return NextResponse.redirect(url);
    56→  }
    57→
    58→  // 提取子域名
    59→  let detectedSkill: ValidSkill | null = null;
    60→  const parts = hostname.split(".");
    61→
    62→  // 检测子域名
    63→  // 格式: {skill}.vibelife.app 或 {skill}.localhost:xxxx
    64→  if (parts.length >= 2) {
    65→    const subdomain = parts[0].toLowerCase();
    66→    if (ENABLED_SKILLS.includes(subdomain as ValidSkill)) {
    67→      detectedSkill = subdomain as ValidSkill;
    68→    }
    69→  }
    70→
    71→  // 检查 URL 参数 ?skill=xxx
    72→  const urlSkillParam = url.searchParams.get("skill");
    73→  const urlSkill = urlSkillParam && VALID_SKILLS.includes(urlSkillParam as ValidSkill)
    74→    ? (urlSkillParam as ValidSkill)
    75→    : null;
    76→
    77→  // 确定最终使用的 skill
    78→  // 优先级: URL 参数 > 子域名 > 路径前缀 > 默认
    79→  let skill: ValidSkill;
    80→  let shouldRewrite = false;
    81→
    82→  if (urlSkill) {
    83→    // URL 参数优先级最高
    84→    skill = urlSkill;
    85→  } else if (detectedSkill) {
    86→    // 有子域名，使用子域名对应的 skill
    87→    skill = detectedSkill;
    88→
    89→    // 如果路径不是共享路由且没有 skill 前缀，需要重写
    90→    if (!isSharedRoute(pathname) && !hasSkillPrefix(pathname)) {
    91→      shouldRewrite = true;
    92→    }
    93→  } else {
    94→    // 无子域名
    95→    // 检查路径是否已有 skill 前缀
    96→    const firstSegment = pathname.split("/")[1];
    97→    if (ENABLED_SKILLS.includes(firstSegment as ValidSkill)) {
    98→      skill = firstSegment as ValidSkill;
    99→    } else {
   100→      skill = DEFAULT_SKILL;
   101→    }
   102→  }
   103→
   104→  // 设置 skill 到请求头
   105→  const requestHeaders = new Headers(request.headers);
   106→  requestHeaders.set("x-vibelife-skill", skill);
   107→
   108→  // 处理 URL 重写（仅当有子域名时）
   109→  if (shouldRewrite && detectedSkill) {
   110→    // 重写路径：添加 skill 前缀
   111→    // / -> /bazi
   112→    // /chat -> /bazi/chat
   113→    const newPathname = pathname === "/" ? `/${detectedSkill}` : `/${detectedSkill}${pathname}`;
   114→    url.pathname = newPathname;
   115→
   116→    const response = NextResponse.rewrite(url, {
   117→      request: { headers: requestHeaders },
   118→    });
   119→
   120→    // 设置 cookie
   121→    response.cookies.set("vibelife-skill", skill, {
   122→      path: "/",
   123→      sameSite: "lax",
   124→      secure: process.env.NODE_ENV === "production",
   125→      maxAge: 60 * 60 * 24 * 365,
   126→    });
   127→
   128→    return response;
   129→  }
   130→
   131→  // 普通请求（无重写）
   132→  const response = NextResponse.next({
   133→    request: { headers: requestHeaders },
   134→  });
   135→
   136→  // 设置 cookie
   137→  response.cookies.set("vibelife-skill", skill, {
   138→    path: "/",
   139→    sameSite: "lax",
   140→    secure: process.env.NODE_ENV === "production",
   141→    maxAge: 60 * 60 * 24 * 365,
   142→  });
   143→
   144→  return response;
   145→}
   146→
   147→export const config = {
   148→  matcher: [
   149→    /*
   150→     * Match all request paths except:
   151→     * - _next/static (static files)
   152→     * - _next/image (image optimization files)
   153→     * - favicon.ico (favicon file)
   154→     * - public folder files
   155→     * - api routes
   156→     */
   157→    "/((?!_next/static|_next/image|favicon.ico|.*\\..*|api).*)",
   158→  ],
   159→};
   160→

