     1→import { NextRequest, NextResponse } from "next/server";
     2→import { API_INTERNAL, API_PATHS } from "@/lib/backend-config";
     3→
     4→/**
     5→ * Auth Me Proxy - 获取当前用户信息
     6→ *
     7→ * 前端调用: GET /api/auth/me
     8→ * 后端端点: GET /api/v1/account/auth/me
     9→ */
    10→
    11→export async function GET(request: NextRequest) {
    12→  try {
    13→    // 构建请求头
    14→    const headers: Record<string, string> = {
    15→      "Content-Type": "application/json",
    16→    };
    17→
    18→    // 转发 Authorization header
    19→    const auth = request.headers.get("Authorization");
    20→    if (auth) {
    21→      headers["Authorization"] = auth;
    22→    }
    23→
    24→    // 转发 Cookie
    25→    const cookie = request.headers.get("Cookie");
    26→    if (cookie) {
    27→      headers["Cookie"] = cookie;
    28→    }
    29→
    30→    // 调用后端 API
    31→    const backendUrl = `${API_INTERNAL}${API_PATHS.AUTH.ME}`;
    32→    const response = await fetch(backendUrl, {
    33→      method: "GET",
    34→      headers,
    35→    });
    36→
    37→    const data = await response.json();
    38→
    39→    return NextResponse.json(data, { status: response.status });
    40→  } catch (error) {
    41→    console.error("Auth me proxy error:", error);
    42→    return NextResponse.json(
    43→      {
    44→        error: "Proxy error",
    45→        detail: error instanceof Error ? error.message : String(error),
    46→      },
    47→      { status: 500 }
    48→    );
    49→  }
    50→}
    51→

