    48→
    49→      // Initialize and prompt
    50→      const googleAccounts = (window as unknown as { google: { accounts: { id: { initialize: (config: { client_id: string; callback: (response: { credential: string }) => void }) => void; prompt: () => void } } } }).google.accounts.id;
    51→
    52→      googleAccounts.initialize({
