@router.post("/auth/email/send-link")
async def send_magic_link(request: EmailAuthRequest, req: Request):
    """Send magic link email with 3-layer protection"""
    try:
        from services.identity import EmailAuthService

        # Get client IP
        ip = req.client.host if req.client else "unknown"
        xff = req.headers.get("x-forwarded-for")
        if xff:
            ip = xff.split(",")[0].strip()

        # Get base URL for magic link
        base_url = str(req.base_url).rstrip("/")

        result = await EmailAuthService.send_magic_link(
            email=request.email,
            turnstile_token=request.turnstile_token,
            ip=ip,
            base_url=base_url
        )
        return result
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))

