"use client";
import { useEffect, useState } from "react";
import { useSearchParams, useRouter } from "next/navigation";

export default function VerifyPage() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const [status, setStatus] = useState<"verifying" | "success" | "error">("verifying");
  const [error, setError] = useState("");

  useEffect(() => {
    const token = searchParams.get("token");
    if (!token) {
      setError("Invalid link");
      setStatus("error");
      return;
    }

    verifyToken(token);
  }, [searchParams]);

  const verifyToken = async (token: string) => {
    try {
      const response = await fetch("/api/auth/email/verify", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ token }),
      });

      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.detail || "Verification failed");
      }

      // Store tokens (same pattern as OAuth)
      localStorage.setItem("vibelife_access_token", data.access_token);
      localStorage.setItem("vibelife_refresh_token", data.refresh_token);

      setStatus("success");
      router.push("/chat");
    } catch (err) {
      setError(err instanceof Error ? err.message : "Verification failed");
      setStatus("error");
    }
  };

  if (status === "verifying") {
    return <div className="text-center p-8">Verifying your link...</div>;
  }

  if (status === "error") {
    return (
      <div className="text-center p-8">
        <h2 className="text-xl font-semibold text-red-600">Verification Failed</h2>
        <p className="text-gray-600 mt-2">{error}</p>
        <a href="/auth/login" className="text-blue-500 mt-4 block">
          Try again
        </a>
      </div>
    );
  }

  return <div className="text-center p-8">Redirecting...</div>;
}
