"use client";

/**
 * Auth Components - v7.1 设计系统
 *
 * 组件列表:
 * - AuthLayout: 左右分栏布局 (PC) / 垂直布局 (Mobile)
 * - BrandVisual: 品牌视觉区 (BreathAura + 标语)
 * - AuthCard: 认证表单卡片
 */

import { memo, ReactNode } from "react";
import { cn } from "@/lib/utils";
import { Card } from "@/components/ui/Card";

// ═══════════════════════════════════════════════════════════════════════════
// AuthLayout - 认证页面布局
// PC: 左侧品牌视觉(50%) + 右侧表单(50%)
// Mobile: Logo + 表单垂直排列
// ═══════════════════════════════════════════════════════════════════════════

interface AuthLayoutProps {
  children: ReactNode;
  className?: string;
}

export const AuthLayout = memo(function AuthLayout({
  children,
  className,
}: AuthLayoutProps) {
  return (
    <div className={cn("min-h-screen bg-bg-primary", className)}>
      <div className="flex min-h-screen">
        {/* Left: Brand Visual - PC only */}
        <div className="hidden lg:flex lg:w-1/2 xl:w-[55%]">
          <BrandVisual />
        </div>

        {/* Right: Form Area */}
        <div className="flex-1 flex flex-col">
          {/* Mobile Header */}
          <header className="lg:hidden h-14 px-4 flex items-center border-b border-subtle/30">
            <div className="flex items-center gap-2">
              <span className="text-2xl">🌟</span>
              <span className="font-serif text-lg font-semibold text-text-primary">VibeLife</span>
            </div>
          </header>

          {/* Form Content */}
          <main className="flex-1 flex items-center justify-center p-4 md:p-8">
            <div className="w-full md:max-w-md">
              {children}
            </div>
          </main>

          {/* Footer */}
          <footer className="py-4 px-4 text-center">
            <p className="text-xs text-text-tertiary">
              © 2026 VibeLife. All rights reserved.
            </p>
          </footer>
        </div>
      </div>
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// BrandVisual - 品牌视觉区
// ═══════════════════════════════════════════════════════════════════════════

export const BrandVisual = memo(function BrandVisual() {
  return (
    <div className="relative w-full h-full bg-gradient-to-br from-vellum-100 via-vellum-50 to-mystic-100 overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-30">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 rounded-full bg-gradient-radial from-mystic-300/50 to-transparent blur-3xl" />
        <div className="absolute bottom-1/4 right-1/4 w-80 h-80 rounded-full bg-gradient-radial from-gold-300/40 to-transparent blur-3xl" />
      </div>

      {/* Breath Aura Animation */}
      <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
        <div className="relative">
          {/* Outer ring */}
          <div className="absolute -inset-20 rounded-full bg-gradient-to-br from-mystic-400/20 to-gold-400/20 animate-pulse-soft" />
          {/* Middle ring */}
          <div className="absolute -inset-12 rounded-full bg-gradient-to-br from-mystic-300/30 to-gold-300/30 animate-pulse-soft animation-delay-150" />
          {/* Inner ring */}
          <div className="absolute -inset-4 rounded-full bg-gradient-to-br from-mystic-200/40 to-gold-200/40 animate-pulse-soft animation-delay-300" />
          {/* Center icon */}
          <div className="relative w-24 h-24 rounded-2xl bg-gradient-to-br from-mystic-500 to-gold-500 flex items-center justify-center shadow-float">
            <span className="text-5xl">🌟</span>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="absolute inset-0 flex flex-col items-center justify-center p-8 text-center">
        <div className="mt-48">
          <h1 className="font-serif text-4xl xl:text-5xl font-bold text-text-primary mb-4">
            VibeLife
          </h1>
          <p className="text-xl text-text-secondary mb-2">
            探索内心世界
          </p>
          <p className="text-base text-text-tertiary md:max-w-md">
            融合东方智慧与现代科技，为你提供个性化的命理分析与人生指引
          </p>
        </div>

        {/* Features */}
        <div className="absolute bottom-12 left-0 right-0 px-8">
          <div className="flex justify-center gap-8">
            {[
              { icon: "🌟", label: "八字命盘" },
              { icon: "🔮", label: "星座解读" },
              { icon: "💎", label: "身份画像" },
            ].map((item) => (
              <div key={item.label} className="flex flex-col items-center gap-2">
                <div className="w-12 h-12 rounded-xl bg-bg-card/80 backdrop-blur-sm shadow-card flex items-center justify-center">
                  <span className="text-2xl">{item.icon}</span>
                </div>
                <span className="text-sm text-text-secondary">{item.label}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// AuthCard - 认证表单卡片
// ═══════════════════════════════════════════════════════════════════════════

interface AuthCardProps {
  title: string;
  subtitle?: string;
  children: ReactNode;
  footer?: ReactNode;
  className?: string;
}

export const AuthCard = memo(function AuthCard({
  title,
  subtitle,
  children,
  footer,
  className,
}: AuthCardProps) {
  return (
    <div className={cn("w-full", className)}>
      {/* Mobile Logo */}
      <div className="lg:hidden flex flex-col items-center mb-8">
        <div className="w-16 h-16 rounded-2xl bg-gradient-to-br from-mystic-500 to-gold-500 flex items-center justify-center shadow-elevated mb-4">
          <span className="text-3xl">🌟</span>
        </div>
        <h1 className="font-serif text-2xl font-bold text-text-primary">VibeLife</h1>
        <p className="text-sm text-text-secondary">探索内心世界</p>
      </div>

      {/* Card */}
      <Card variant="default" padding="lg" hover={false} className="lg:shadow-elevated">
        {/* Header */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold text-text-primary mb-1">{title}</h2>
          {subtitle && (
            <p className="text-sm text-text-secondary">{subtitle}</p>
          )}
        </div>

        {/* Form Content */}
        {children}
      </Card>

      {/* Footer */}
      {footer && (
        <div className="mt-6 text-center">
          {footer}
        </div>
      )}
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// AuthDivider - 分隔线
// ═══════════════════════════════════════════════════════════════════════════

interface AuthDividerProps {
  text?: string;
  className?: string;
}

export const AuthDivider = memo(function AuthDivider({
  text = "或",
  className,
}: AuthDividerProps) {
  return (
    <div className={cn("relative my-6", className)}>
      <div className="absolute inset-0 flex items-center">
        <div className="w-full border-t border-subtle" />
      </div>
      <div className="relative flex justify-center text-xs">
        <span className="px-3 bg-bg-card text-text-tertiary">{text}</span>
      </div>
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// AuthLink - 认证页面链接
// ═══════════════════════════════════════════════════════════════════════════

interface AuthLinkProps {
  text: string;
  linkText: string;
  href: string;
  onClick?: () => void;
  className?: string;
}

export const AuthLink = memo(function AuthLink({
  text,
  linkText,
  href,
  onClick,
  className,
}: AuthLinkProps) {
  return (
    <p className={cn("text-sm text-text-secondary", className)}>
      {text}{" "}
      <a
        href={href}
        onClick={(e) => {
          if (onClick) {
            e.preventDefault();
            onClick();
          }
        }}
        className="text-accent-primary font-medium hover:underline"
      >
        {linkText}
      </a>
    </p>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// Exports
// ═══════════════════════════════════════════════════════════════════════════

export default {
  AuthLayout,
  BrandVisual,
  AuthCard,
  AuthDivider,
  AuthLink,
};

