import { test, expect } from '@playwright/test';

test.describe('认证流程测试', () => {
  test('登录页表单与按钮', async ({ page }) => {
    await page.goto('/auth/login');
    await page.waitForLoadState('networkidle');

    await expect(page.getByPlaceholder('your@email.com')).toBeVisible();
    await expect(page.getByPlaceholder('输入密码')).toBeVisible();

    await page.getByPlaceholder('your@email.com').fill('test@example.com');
    await page.getByPlaceholder('输入密码').fill('password123');

    const loginButton = page.getByRole('button', { name: '登录' });
    await expect(loginButton).toBeEnabled();
    await loginButton.click();

    const googleButton = page.getByRole('button', { name: 'Continue with Google' });
    const appleButton = page.getByRole('button', { name: 'Continue with Apple' });
    if (await googleButton.isVisible() && await googleButton.isEnabled()) await googleButton.click();
    if (await appleButton.isVisible() && await appleButton.isEnabled()) await appleButton.click();

    await page.getByRole('link', { name: '忘记密码？' }).click();
    await page.waitForURL(/forgot-password/);

    await page.goto('/auth/login');
    await page.waitForLoadState('networkidle');
    await page.getByRole('link', { name: '注册' }).click();
    await page.waitForURL(/register/);
  });

  test('注册页邮箱与手机号模式', async ({ page }) => {
    await page.goto('/auth/register');
    await page.waitForLoadState('networkidle');

    const emailMode = page.getByRole('button', { name: '邮箱注册' });
    const phoneMode = page.getByRole('button', { name: '手机号注册' });
    await emailMode.click();

    await page.getByPlaceholder('你想被怎么称呼？').fill('测试用户');
    await page.getByPlaceholder('your@email.com').fill('test@example.com');
    await page.getByPlaceholder('至少6个字符').fill('password123');
    await page.getByPlaceholder('再次输入密码').fill('password123');

    const registerButton = page.getByRole('button', { name: '注册', exact: true });
    if (await registerButton.isVisible() && await registerButton.isEnabled()) {
      await registerButton.click();
    }

    await phoneMode.click();
    await page.getByPlaceholder('请输入手机号').fill('13800138000');
  });

  test('找回密码页流程', async ({ page }) => {
    await page.goto('/auth/forgot-password');
    await page.waitForLoadState('networkidle');

    const emailMode = page.getByRole('button', { name: '邮箱找回' });
    const phoneMode = page.getByRole('button', { name: '手机号找回' });
    await emailMode.click();

    await page.getByPlaceholder('your@email.com').fill('test@example.com');
    const sendButton = page.getByRole('button', { name: '发送重置链接' });
    await expect(sendButton).toBeEnabled();
    await sendButton.click();

    await phoneMode.click();
    await page.getByPlaceholder('请输入手机号').fill('13800138000');
  });

  test('重置密码页表单', async ({ page }) => {
    await page.goto('/auth/reset-password');
    await page.waitForLoadState('networkidle');

    const newPassword = page.getByPlaceholder('至少6个字符');
    const confirmPassword = page.getByPlaceholder('再次输入新密码');

    if (await newPassword.isVisible()) {
      await newPassword.fill('newpassword123');
      await confirmPassword.fill('newpassword123');
      const resetButton = page.getByRole('button', { name: '重置密码' });
      if (await resetButton.isVisible()) await resetButton.click();
    }
  });
});

