 */

const API_BASE = process.env.VIBELIFE_API_INTERNAL || 'http://127.0.0.1:8100';
const WEB_URL = process.env.TEST_URL || 'http://localhost:8232';
const TEST_EMAIL = `test_${Date.now()}@example.com`;
const TEST_PASSWORD = 'TestPassword123!';

// ═══════════════════════════════════════════════════════════════════════════
// UI Tests - Login Page
// ═══════════════════════════════════════════════════════════════════════════

test.describe('Email Auth UI Tests', () => {
  test('Login page renders email form and OAuth buttons', async ({ page }) => {
    await page.goto(`${WEB_URL}/auth/login`);
    await page.waitForLoadState('networkidle');

    // Check page title
    await expect(page.getByText('欢迎来到 VibeLife')).toBeVisible();

    // Check email input
    const emailInput = page.getByPlaceholder('Email address');
    await expect(emailInput).toBeVisible();

    // Check password input
    const passwordInput = page.getByPlaceholder(/Password/);
    await expect(passwordInput).toBeVisible();

    // Check submit button
    const signInButton = page.getByRole('button', { name: /Sign In/i });
    await expect(signInButton).toBeVisible();

    // Check OAuth buttons
    const googleButton = page.getByRole('button', { name: /Continue with Google/i });
    const appleButton = page.getByRole('button', { name: /Continue with Apple/i });
    await expect(googleButton).toBeVisible();
    await expect(appleButton).toBeVisible();

    // Check divider
    await expect(page.getByText('or continue with')).toBeVisible();

    console.log('Login page UI elements verified');
  });

  test('Email form validation - empty fields', async ({ page }) => {
    await page.goto(`${WEB_URL}/auth/login`);
    await page.waitForLoadState('networkidle');

    // Try to submit without filling fields
    const signInButton = page.getByRole('button', { name: /Sign In/i });

    // Button should be disabled when fields are empty
    await expect(signInButton).toBeDisabled();
  });

  test('Email form - fill and switch modes', async ({ page }) => {
    await page.goto(`${WEB_URL}/auth/login`);
    await page.waitForLoadState('networkidle');

    // Fill email and password
    await page.getByPlaceholder('Email address').fill('test@example.com');
    await page.getByPlaceholder(/Password/).fill('TestPassword123');

    // Button should now be enabled
    const signInButton = page.getByRole('button', { name: /Sign In/i });
    await expect(signInButton).toBeEnabled();

    // Switch to register mode
    const createAccountLink = page.getByRole('button', { name: /Create one/i });
    await createAccountLink.click();

    // Button should now say "Create Account"
    const createButton = page.getByRole('button', { name: /Create Account/i });
    await expect(createButton).toBeVisible();

    // Switch back to login
    const signInLink = page.getByRole('button', { name: /Sign in/i });
    await signInLink.click();

    // Button should be back to Sign In
    await expect(page.getByRole('button', { name: /Sign In/i })).toBeVisible();
  });

  test('Email form - registration flow UI', async ({ page }) => {
    await page.goto(`${WEB_URL}/auth/login`);
    await page.waitForLoadState('networkidle');

    // Switch to register mode
    await page.getByRole('button', { name: /Create one/i }).click();

    // Fill registration form
    const uniqueEmail = `uitest_${Date.now()}@example.com`;
    await page.getByPlaceholder('Email address').fill(uniqueEmail);
    await page.getByPlaceholder(/Password.*8/).fill('TestPassword123!');

    // Click Create Account
    await page.getByRole('button', { name: /Create Account/i }).click();

    // Wait for response
    await page.waitForTimeout(2000);

    // Should show verification code input or error
    const verifyHeading = page.getByText(/Verification code sent/i);
    const codeInput = page.getByPlaceholder(/6-digit code/i);

    // Either we're on verify step or got an error
    const isOnVerifyStep = await verifyHeading.isVisible().catch(() => false) ||
                           await codeInput.isVisible().catch(() => false);

    if (isOnVerifyStep) {
      console.log('Reached verification step successfully');
      await expect(codeInput).toBeVisible();

      // Check Back button
      await expect(page.getByRole('button', { name: /Back/i })).toBeVisible();

      // Check Verify button (disabled initially)
      const verifyButton = page.getByRole('button', { name: /Verify/i });
      await expect(verifyButton).toBeDisabled();

      // Fill code
      await codeInput.fill('123456');
      await expect(verifyButton).toBeEnabled();
    } else {
      console.log('Did not reach verify step - checking for error message');
      // Check for any error message displayed
      const errorMessage = page.locator('.text-red-500');
      if (await errorMessage.isVisible()) {
        console.log('Error:', await errorMessage.textContent());
      }
    }
  });

  test('Take screenshot of login page', async ({ page }) => {
    await page.goto(`${WEB_URL}/auth/login`);
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(500);

    await page.screenshot({ path: '.playwright-mcp/login-page-email-auth.png', fullPage: true });
    console.log('Screenshot saved to .playwright-mcp/login-page-email-auth.png');
  });
});

