const { chromium } = require('playwright');
const fs = require('fs');

(async () => {
  const browser = await chromium.launch();
  const page = await browser.newPage();

  const consoleMessages = [];
  page.on('console', msg => {
    consoleMessages.push(`[${msg.type()}] ${msg.text()}`);
  });

  try {
    await page.goto('http://106.37.170.238:8232/');
    await page.waitForLoadState('networkidle');

    const content = await page.evaluate(() => document.body.innerText);

    fs.writeFileSync('/home/aiscend/work/home_snapshot.md', content);
    fs.writeFileSync('/home/aiscend/work/home_console.txt', consoleMessages.join('\n'));

  } catch (error) {
    console.error('Error:', error);
    fs.writeFileSync('/home/aiscend/work/home_error.txt', error.toString());
  } finally {
    await browser.close();
  }
})();
