"use client";

import { cn } from "@/lib/utils";
import { type SkillType } from "./LuminousPaper";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// BreathAura - LUMINOUS PAPER Design System
// å‘¼å¸å…‰æ™•ï¼šæ ¹æ®äº”è¡Œå±æ€§å˜è‰²çš„åŠ¨æ€å…‰æ™•
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface BreathAuraProps {
  size?: "sm" | "md" | "lg" | "xl";
  skill?: SkillType;
  intensity?: "low" | "medium" | "high";
  position?: "center" | "top" | "bottom" | "left" | "right";
  className?: string;
  animate?: boolean;
}

const sizeClasses = {
  sm: "w-32 h-32",
  md: "w-48 h-48",
  lg: "w-64 h-64",
  xl: "w-96 h-96",
};

const positionClasses = {
  center: "top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2",
  top: "top-0 left-1/2 -translate-x-1/2 -translate-y-1/2",
  bottom: "bottom-0 left-1/2 -translate-x-1/2 translate-y-1/2",
  left: "top-1/2 left-0 -translate-x-1/2 -translate-y-1/2",
  right: "top-1/2 right-0 translate-x-1/2 -translate-y-1/2",
};

const skillGlowColors: Record<SkillType, string> = {
  bazi: "rgba(122, 63, 26, 0.25)",
  zodiac: "rgba(60, 58, 122, 0.2)",
  mbti: "rgba(15, 118, 110, 0.2)",
  attach: "rgba(177, 60, 65, 0.2)",
  career: "rgba(14, 116, 144, 0.2)",
};

const intensityOpacity = {
  low: 0.5,
  medium: 0.75,
  high: 1,
};

export function BreathAura({
  size = "md",
  skill = "bazi",
  intensity = "medium",
  position = "center",
  className,
  animate = true,
}: BreathAuraProps) {
  return (
    <div
      className={cn(
        "absolute rounded-full blur-3xl pointer-events-none",
        sizeClasses[size],
        positionClasses[position],
        animate && "animate-breath",
        className
      )}
      style={{
        background: skillGlowColors[skill],
        opacity: intensityOpacity[intensity],
      }}
    />
  );
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// MultiBreathAura - Multiple layered auras for richer effect
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface MultiBreathAuraProps {
  skill?: SkillType;
  className?: string;
}

export function MultiBreathAura({ skill = "bazi", className }: MultiBreathAuraProps) {
  return (
    <div className={cn("absolute inset-0 overflow-hidden pointer-events-none", className)}>
      <BreathAura
        skill={skill}
        size="xl"
        position="top"
        intensity="low"
        className="opacity-60"
      />
      <BreathAura
        skill={skill}
        size="lg"
        position="center"
        intensity="medium"
        className="opacity-40 animation-delay-1000"
      />
      <BreathAura
        skill={skill}
        size="md"
        position="bottom"
        intensity="low"
        className="opacity-30 animation-delay-2000"
      />
    </div>
  );
}

export default BreathAura;

/**
 * ArchetypeRadar - 12 åŸå‹é›·è¾¾å›¾ç»„ä»¶
 *
 * åŸºäºè®¾è®¡æ–‡æ¡£ v5.0 å’Œå‰ç«¯è§„èŒƒ v7.1
 * å±•ç¤ºç”¨æˆ·åœ¨ 12 ä¸ªåŸå‹ä¸Šçš„å¾—åˆ†åˆ†å¸ƒ
 */

'use client'

import { useMemo } from 'react'
import { cn } from '@/lib/utils'
import {
  type ArchetypeType,
  type ArchetypeScores,
  ARCHETYPE_META,
  getAllArchetypes,
  getArchetypeColor,
} from '@/types/vibe-id'

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Types
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface ArchetypeRadarProps {
  scores: ArchetypeScores
  /** Core/ä¸»åŸå‹é«˜äº® */
  highlightArchetype?: ArchetypeType
  /** Shadow/é˜´å½±åŸå‹é«˜äº® (ä½¿ç”¨è­¦å‘Šè‰²å’Œè™šçº¿) */
  shadowArchetype?: ArchetypeType
  size?: number
  showLabels?: boolean
  showScores?: boolean
  interactive?: boolean
  className?: string
  onArchetypeClick?: (archetype: ArchetypeType) => void
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Constants
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const ARCHETYPES_ORDER: ArchetypeType[] = [
  'Hero', 'Ruler', 'Sage', 'Caregiver',
  'Creator', 'Outlaw', 'Magician', 'Lover',
  'Jester', 'Regular', 'Innocent', 'Explorer',
]

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Helper Functions
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function polarToCartesian(
  centerX: number,
  centerY: number,
  radius: number,
  angleInDegrees: number
): { x: number; y: number } {
  const angleInRadians = ((angleInDegrees - 90) * Math.PI) / 180
  // Round to 2 decimal places to avoid hydration mismatch between server and client
  return {
    x: Math.round((centerX + radius * Math.cos(angleInRadians)) * 100) / 100,
    y: Math.round((centerY + radius * Math.sin(angleInRadians)) * 100) / 100,
  }
}

function getPolygonPoints(
  scores: ArchetypeScores,
  center: number,
  maxRadius: number
): string {
  return ARCHETYPES_ORDER.map((archetype, index) => {
    const angle = (360 / 12) * index
    const score = scores[archetype] || 0
    const radius = (score / 100) * maxRadius
    const { x, y } = polarToCartesian(center, center, radius, angle)
    return `${x},${y}`
  }).join(' ')
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Main Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export function ArchetypeRadar({
  scores,
  highlightArchetype,
  shadowArchetype,
  size = 300,
  showLabels = true,
  showScores = false,
  interactive = true,
  className,
  onArchetypeClick,
}: ArchetypeRadarProps) {
  const center = size / 2
  const maxRadius = size * 0.35
  const labelRadius = size * 0.45

  const polygonPoints = useMemo(
    () => getPolygonPoints(scores, center, maxRadius),
    [scores, center, maxRadius]
  )

  const gridLevels = [20, 40, 60, 80, 100]

  return (
    <div className={cn('relative', className)}>
      <svg
        width={size}
        height={size}
        viewBox={`0 0 ${size} ${size}`}
        className="overflow-visible"
      >
        {/* èƒŒæ™¯ç½‘æ ¼ */}
        <g className="grid-lines">
          {gridLevels.map((level) => {
            const radius = (level / 100) * maxRadius
            const points = ARCHETYPES_ORDER.map((_, index) => {
              const angle = (360 / 12) * index
              const { x, y } = polarToCartesian(center, center, radius, angle)
              return `${x},${y}`
            }).join(' ')

            return (
              <polygon
                key={level}
                points={points}
                fill="none"
                stroke="var(--ink-200)"
                strokeWidth="1"
                opacity={0.5}
              />
            )
          })}

          {ARCHETYPES_ORDER.map((_, index) => {
            const angle = (360 / 12) * index
            const { x, y } = polarToCartesian(center, center, maxRadius, angle)
            return (
              <line
                key={index}
                x1={center}
                y1={center}
                x2={x}
                y2={y}
                stroke="var(--ink-200)"
                strokeWidth="1"
                opacity={0.3}
              />
            )
          })}
        </g>

        {/* æ•°æ®å¤šè¾¹å½¢ */}
        <polygon
          points={polygonPoints}
          fill="var(--accent)"
          fillOpacity={0.2}
          stroke="var(--accent)"
          strokeWidth="2"
          className="transition-all duration-300"
        />

        {/* æ•°æ®ç‚¹ */}
        {ARCHETYPES_ORDER.map((archetype, index) => {
          const angle = (360 / 12) * index
          const score = scores[archetype] || 0
          const radius = (score / 100) * maxRadius
          const { x, y } = polarToCartesian(center, center, radius, angle)
          const isHighlighted = archetype === highlightArchetype
          const isShadow = archetype === shadowArchetype
          const color = getArchetypeColor(archetype)

          // Shadow åŸå‹ä½¿ç”¨æ©™è‰²/è­¦å‘Šè‰²
          const pointColor = isShadow
            ? 'var(--orange-500, #f97316)'
            : isHighlighted
              ? color
              : 'var(--accent)'

          return (
            <g key={archetype}>
              {/* Shadow åŸå‹æ·»åŠ è™šçº¿å¤–ç¯ */}
              {isShadow && (
                <circle
                  cx={x}
                  cy={y}
                  r={10}
                  fill="none"
                  stroke="var(--orange-500, #f97316)"
                  strokeWidth="2"
                  strokeDasharray="3,2"
                  opacity={0.6}
                  className="animate-pulse"
                />
              )}
              <circle
                cx={x}
                cy={y}
                r={isHighlighted || isShadow ? 6 : 4}
                fill={pointColor}
                stroke="white"
                strokeWidth="2"
                className={cn(
                  'transition-all duration-200',
                  interactive && 'cursor-pointer hover:r-6'
                )}
                onClick={() => interactive && onArchetypeClick?.(archetype)}
              />
            </g>
          )
        })}

        {/* æ ‡ç­¾ */}
        {showLabels &&
          ARCHETYPES_ORDER.map((archetype, index) => {
            const angle = (360 / 12) * index
            const { x, y } = polarToCartesian(center, center, labelRadius, angle)
            const meta = ARCHETYPE_META[archetype]
            const isHighlighted = archetype === highlightArchetype
            const isShadow = archetype === shadowArchetype
            const score = scores[archetype] || 0

            let textAnchor: 'start' | 'middle' | 'end' = 'middle'
            let dx = 0
            if (angle > 30 && angle < 150) {
              textAnchor = 'start'
              dx = 5
            } else if (angle > 210 && angle < 330) {
              textAnchor = 'end'
              dx = -5
            }

            return (
              <g
                key={archetype}
                className={cn(
                  'transition-opacity duration-200',
                  interactive && 'cursor-pointer hover:opacity-80'
                )}
                onClick={() => interactive && onArchetypeClick?.(archetype)}
              >
                <text
                  x={x}
                  y={y - 8}
                  textAnchor="middle"
                  className="text-base"
                  style={{ fontSize: isHighlighted || isShadow ? '18px' : '14px' }}
                >
                  {meta.emoji}
                </text>

                <text
                  x={x + dx}
                  y={y + 10}
                  textAnchor={textAnchor}
                  className={cn(
                    'text-xs fill-current',
                    isHighlighted && 'text-ink-800 font-medium',
                    isShadow && 'text-orange-600 font-medium',
                    !isHighlighted && !isShadow && 'text-ink-500'
                  )}
                >
                  {meta.name}
                </text>

                {/* Shadow åŸå‹æ·»åŠ å¼ åŠ›æ ‡è®° */}
                {isShadow && (
                  <text
                    x={x + dx}
                    y={y + 22}
                    textAnchor={textAnchor}
                    className="text-[10px] fill-current text-orange-500"
                  >
                    âš¡ Shadow
                  </text>
                )}

                {showScores && !isShadow && (
                  <text
                    x={x + dx}
                    y={y + 22}
                    textAnchor={textAnchor}
                    className="text-[10px] fill-current text-ink-400"
                  >
                    {score}
                  </text>
                )}
              </g>
            )
          })}
      </svg>
    </div>
  )
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Compact List View
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface ArchetypeListProps {
  scores: ArchetypeScores
  limit?: number
  showAll?: boolean
  className?: string
  onArchetypeClick?: (archetype: ArchetypeType) => void
}

export function ArchetypeList({
  scores,
  limit = 5,
  showAll = false,
  className,
  onArchetypeClick,
}: ArchetypeListProps) {
  const sortedArchetypes = useMemo(() => {
    return getAllArchetypes()
      .map((archetype) => ({
        archetype,
        score: scores[archetype] || 0,
        meta: ARCHETYPE_META[archetype],
      }))
      .sort((a, b) => b.score - a.score)
  }, [scores])

  const displayArchetypes = showAll ? sortedArchetypes : sortedArchetypes.slice(0, limit)

  return (
    <div className={cn('space-y-2', className)}>
      {displayArchetypes.map(({ archetype, score, meta }, index) => {
        const color = getArchetypeColor(archetype)
        const isTop3 = index < 3

        return (
          <div
            key={archetype}
            className={cn(
              'flex items-center gap-3 p-2 rounded-lg transition-colors',
              onArchetypeClick && 'cursor-pointer hover:bg-vellum-100'
            )}
            onClick={() => onArchetypeClick?.(archetype)}
          >
            <span
              className={cn(
                'w-5 h-5 rounded-full flex items-center justify-center text-xs font-medium',
                isTop3 ? 'bg-accent text-white' : 'bg-vellum-100 text-ink-500'
              )}
            >
              {index + 1}
            </span>

            <span className="text-lg">{meta.emoji}</span>

            <div className="flex-1">
              <span className="text-sm text-ink-800">{meta.name}</span>
              <span className="text-xs text-ink-500 ml-1">/ {meta.nickname}</span>
            </div>

            <div className="w-20 h-2 bg-vellum-100 rounded-full overflow-hidden">
              <div
                className="h-full rounded-full transition-all duration-300"
                style={{
                  width: `${score}%`,
                  backgroundColor: color,
                }}
              />
            </div>

            <span className="text-xs text-ink-500 w-8 text-right">{score}</span>
          </div>
        )
      })}
    </div>
  )
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Archetype Detail Card
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface ArchetypeDetailProps {
  archetype: ArchetypeType
  score?: number
  isCore?: boolean
  className?: string
}

export function ArchetypeDetail({
  archetype,
  score,
  isCore = false,
  className,
}: ArchetypeDetailProps) {
  const meta = ARCHETYPE_META[archetype]
  const color = getArchetypeColor(archetype)

  return (
    <div
      className={cn(
        'p-4 rounded-xl border transition-all',
        isCore ? 'bg-accent/5 border-accent/20' : 'bg-white border-vellum-200/50',
        className
      )}
    >
      <div className="flex items-start gap-3 mb-3">
        <div
          className="w-12 h-12 rounded-xl flex items-center justify-center text-2xl"
          style={{ background: `${color}20` }}
        >
          {meta.emoji}
        </div>
        <div className="flex-1">
          <div className="flex items-center gap-2">
            <h3 className="font-medium text-ink-800">{meta.name}</h3>
            {isCore && (
              <span className="px-1.5 py-0.5 bg-accent text-white text-xs rounded">Core</span>
            )}
          </div>
          <p className="text-sm text-ink-500">{meta.nickname}</p>
        </div>
        {score !== undefined && (
          <div className="text-right">
            <span className="text-lg font-medium text-ink-800">{score}</span>
            <span className="text-xs text-ink-500">/100</span>
          </div>
        )}
      </div>

      <p className="text-sm text-ink-500 mb-3">&ldquo;{meta.slogan}&rdquo;</p>

      <div className="grid grid-cols-2 gap-2 mb-3">
        <div className="p-2 rounded-lg bg-vellum-50">
          <p className="text-xs text-ink-500 mb-1">æ ¸å¿ƒæ¸´æœ›</p>
          <p className="text-sm text-ink-800">{meta.coreDesire}</p>
        </div>
        <div className="p-2 rounded-lg bg-vellum-50">
          <p className="text-xs text-ink-500 mb-1">æ ¸å¿ƒææƒ§</p>
          <p className="text-sm text-ink-800">{meta.coreFear}</p>
        </div>
      </div>

      <div className="mb-3">
        <p className="text-xs text-ink-500 mb-1">è¶…èƒ½åŠ›</p>
        <div className="flex flex-wrap gap-1">
          {meta.superpower.map((power) => (
            <span
              key={power}
              className="px-2 py-0.5 bg-vellum-100 rounded-full text-xs text-ink-700"
            >
              {power}
            </span>
          ))}
        </div>
