"use client";

import { useState, useMemo } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { TriToggle, type TriToggleValue } from "@/components/ui/TriToggle";
import { cn } from "@/lib/utils";

/**
 * IdentityPrism - ä¸‰å…ƒèº«ä»½æ£±é•œ
 *
 * æ ¸å¿ƒç†å¿µï¼š"äººæ˜¯å¤šé¢çš„ã€‚ä½ è¢«ç†è§£ã€è¢«å…è®¸ã€è¢«èµ‹èƒ½ã€‚"
 *
 * ä¸‰å…ƒè§†è§’:
 * - Inner (å†…åœ¨): å†…å¿ƒæœ€åœ¨æ„çš„
 * - Core (æ ¸å¿ƒ): çœŸæ­£ç¨³å®šçš„é©±åŠ¨åŠ›
 * - Outer (å¤–åœ¨): ç»™äººçš„æ„Ÿè§‰
 */

export interface PrismContent {
  inner: {
    title: string;
    content: string;
    evidence?: string;
  };
  core: {
    title: string;
    content: string;
    evidence?: string;
  };
  outer: {
    title: string;
    content: string;
    evidence?: string;
  };
}

interface IdentityPrismProps {
  content: PrismContent;
  className?: string;
}

// å…‰æ™•é…ç½®éšè§†è§’å˜åŒ–
const AURA_CONFIG: Record<
  TriToggleValue,
  {
    scale: number;
    opacity: number;
    breathDuration: string;
  }
> = {
  inner: { scale: 0.8, opacity: 0.25, breathDuration: "20s" },
  core: { scale: 1, opacity: 0.35, breathDuration: "24s" }, // å‡ ä¹é™æ­¢
  outer: { scale: 1.2, opacity: 0.45, breathDuration: "16s" },
};

// è§†è§’å¼•å¯¼è¯­
const PERSPECTIVE_INTRO: Record<TriToggleValue, string> = {
  inner: "åœ¨ä½ å¿ƒé‡Œï¼Œå…¶å®æœ€åœ¨æ„çš„æ˜¯...",
  core: "ä½ çœŸæ­£ç¨³å®šçš„é©±åŠ¨åŠ›æ˜¯...",
  outer: "ä½ ç»™äººçš„æ„Ÿè§‰æ˜¯...",
};

export function IdentityPrism({ content, className }: IdentityPrismProps) {
  const [perspective, setPerspective] = useState<TriToggleValue>("core");

  const currentContent = content[perspective];
  const auraConfig = AURA_CONFIG[perspective];
  const intro = PERSPECTIVE_INTRO[perspective];

  return (
    <div className={cn("relative", className)}>
      {/* æ ‡é¢˜ */}
      <div className="text-center mb-4">
        <h2 className="font-serif text-lg font-semibold tracking-tight">
          IDENTITY PRISM
        </h2>
        <p className="text-xs text-muted-foreground mt-1">èº«ä»½æ£±é•œ</p>
      </div>

      {/* TriToggle åˆ‡æ¢å™¨ */}
      <div className="flex justify-center mb-6">
        <TriToggle value={perspective} onChange={setPerspective} size="md" />
      </div>

      {/* å†…å®¹å¡ç‰‡ + å…‰æ™• */}
      <div className="relative bg-bg-card border border-subtle shadow-soft rounded-xl p-6 overflow-hidden">
        {/* åŠ¨æ€å…‰æ™•èƒŒæ™¯ */}
        <motion.div
          className="absolute inset-0 pointer-events-none"
          initial={false}
          animate={{
            scale: [auraConfig.scale, auraConfig.scale * 1.05, auraConfig.scale],
            opacity: [auraConfig.opacity, auraConfig.opacity * 0.8, auraConfig.opacity],
          }}
          transition={{
            duration: parseInt(auraConfig.breathDuration),
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
