    49→  --float-duration: 20s;    /* 修正: 原 6s */
    50→  --breath-scale: 1.015;    /* 修正: 原 1.05, 最大 1.02 */
    51→
    52→  /* Easing Functions */
    53→  --ease-out-expo: cubic-bezier(0.16, 1, 0.3, 1);
    54→  --ease-spring: cubic-bezier(0.34, 1.56, 0.64, 1);
    55→  --ease-smooth: cubic-bezier(0.4, 0, 0.2, 1);
    56→}
