   119→/* ───────────────────────────────────────────────────────────────────────────
   120→   Base Styles
   121→   ─────────────────────────────────────────────────────────────────────────── */
   122→@layer base {
   123→  * {
   124→    @apply border-border;
   125→  }
   126→
   127→  html {
   128→    scroll-behavior: smooth;
   129→    -webkit-font-smoothing: antialiased;
   130→    -moz-osx-font-smoothing: grayscale;
   131→  }
   132→
   133→  body {
   134→    @apply bg-background text-foreground font-sans;
   135→    min-height: 100vh;
   136→  }
   137→
   138→  /* Typography defaults */
   139→  h1, h2, h3, h4, h5, h6 {
   140→    @apply font-serif tracking-tight;
   141→  }
   142→
   143→  /* Selection */
   144→  ::selection {
   145→    @apply bg-accent/20 text-foreground;
   146→  }
   147→}
   148→
   149→/* ───────────────────────────────────────────────────────────────────────────
   150→   Vellum Background (Luminous Paper)
   151→   ─────────────────────────────────────────────────────────────────────────── */
   152→@layer components {
   153→  .vellum-bg {
   154→    @apply relative bg-vellum;
   155→  }
   156→
   157→  .vellum-bg::before {
   158→    content: "";
   159→    @apply absolute inset-0 pointer-events-none;
   160→    opacity: 0.55;
   161→    background:
   162→      radial-gradient(circle at 20% 10%, var(--glow-earth), transparent 45%),
   163→      radial-gradient(circle at 80% 30%, var(--glow-water), transparent 50%),
   164→      radial-gradient(circle at 50% 70%, var(--glow-wood), transparent 55%),
   165→      radial-gradient(rgba(28, 26, 23, 0.03) 1px, transparent 1px);
   166→    background-size: auto, auto, auto, 28px 28px;
   167→    mix-blend-mode: multiply;
   168→  }
   169→
   170→  .dark .vellum-bg {
   171→    @apply bg-background;
   172→  }
   173→
   174→  .dark .vellum-bg::before {
   175→    opacity: 0.3;
   176→    mix-blend-mode: screen;
   177→  }
   178→
   179→  /* Luminous Paper with Skill glow */
   180→  .luminous-paper {
   181→    @apply relative;
   182→    background: hsl(var(--skill-bg));
   183→  }
   184→
   185→  .luminous-paper::before {
   186→    content: "";
   187→    @apply absolute inset-0 pointer-events-none;
   188→    background: radial-gradient(
   189→      ellipse 80% 50% at 50% 0%,
   190→      var(--skill-glow),
   191→      transparent 70%
   192→    );
   193→  }
   194→}
   195→
   196→/* ───────────────────────────────────────────────────────────────────────────
   197→   Glass Effects (Glassmorphism)
   198→   ─────────────────────────────────────────────────────────────────────────── */
   199→@layer components {
   200→  .glass {
   201→    @apply backdrop-blur-xl;
   202→    background: hsl(var(--background) / 0.7);
   203→    border: 1px solid hsl(var(--border) / 0.5);
   204→  }
   205→
   206→  .glass-card {
   207→    @apply backdrop-blur-lg rounded-xl;
   208→    background: hsl(var(--card) / 0.8);
   209→    border: 1px solid hsl(var(--border) / 0.3);
   210→    box-shadow:
   211→      0 4px 24px rgba(28, 26, 23, 0.06),
   212→      inset 0 1px 0 rgba(255, 255, 255, 0.1);
   213→  }
   214→
   215→  .glass-card:hover {
   216→    box-shadow:
   217→      0 8px 32px rgba(28, 26, 23, 0.1),
   218→      inset 0 1px 0 rgba(255, 255, 255, 0.15);
   219→  }
   220→
   221→  .glass-subtle {
   222→    @apply backdrop-blur-md;
   223→    background: hsl(var(--background) / 0.85);
   224→  }
   225→
   226→  /* Capsule (Omnibar style) */
   227→  .glass-capsule {
   228→    @apply backdrop-blur-xl rounded-full;
   229→    background: hsl(var(--card) / 0.9);
   230→    border: 1px solid hsl(var(--border) / 0.4);
   231→    box-shadow:
   232→      0 4px 20px rgba(28, 26, 23, 0.08),
   233→      inset 0 1px 0 rgba(255, 255, 255, 0.1);
   234→  }
   235→}
   236→
   237→/* ───────────────────────────────────────────────────────────────────────────
   238→   Button Styles
   239→   ─────────────────────────────────────────────────────────────────────────── */
   240→@layer components {
   241→  .btn {
   242→    @apply inline-flex items-center justify-center gap-2 font-sans font-medium
   243→           transition-all duration-200 ease-out-expo
   244→           focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2
   245→           disabled:pointer-events-none disabled:opacity-50;
   246→  }
   247→
   248→  .btn-primary {
   249→    @apply btn bg-ink text-vellum rounded-full px-6 py-3
   250→           hover:bg-ink/90 active:scale-[0.98];
   251→  }
   252→
   253→  .btn-secondary {
   254→    @apply btn bg-card text-foreground border border-border rounded-full px-6 py-3
   255→           hover:bg-muted active:scale-[0.98];
   256→  }
   257→
   258→  .btn-ghost {
   259→    @apply btn text-foreground rounded-lg px-4 py-2
   260→           hover:bg-muted/50 active:scale-[0.98];
   261→  }
   262→
   263→  .btn-gold {
