   271→  }
   272→
   273→  .btn-lg {
   274→    @apply text-lg px-8 py-4;
   275→  }
   276→}
   277→
   278→/* ───────────────────────────────────────────────────────────────────────────
   279→   Card Styles
   280→   ─────────────────────────────────────────────────────────────────────────── */
   281→@layer components {
   282→  .card {
   283→    @apply bg-card text-card-foreground rounded-xl border border-border
   284→           shadow-card transition-shadow duration-200;
   285→  }
   286→
   287→  .card:hover {
   288→    @apply shadow-card-hover;
   289→  }
   290→
   291→  .card-header {
   292→    @apply p-6 pb-0;
   293→  }
   294→
   295→  .card-title {
