   324→}
   325→
   326→/* ───────────────────────────────────────────────────────────────────────────
   327→   Input Styles
   328→   ─────────────────────────────────────────────────────────────────────────── */
   329→@layer components {
   330→  .input {
   331→    @apply w-full bg-card text-foreground rounded-lg px-4 py-3
   332→           border border-border
   333→           placeholder:text-muted-foreground
   334→           focus:outline-none focus:ring-2 focus:ring-ring focus:border-transparent
   335→           transition-all duration-200;
   336→  }
   337→
   338→  .input-capsule {
   339→    @apply input rounded-full px-6;
   340→  }
   341→
   342→  .select {
   343→    @apply input appearance-none cursor-pointer
   344→           bg-[url('data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%2224%22%20height%3D%2224%22%20viewBox%3D%220%200%2024%2024%22%20fill%3D%22none%22%20stroke%3D%22%236D655B%22%20stroke-width%3D%222%22%20stroke-linecap%3D%22round%22%20stroke-linejoin%3D%22round%22%3E%3Cpath%20d%3D%22m6%209%206%206%206-6%22%2F%3E%3C%2Fsvg%3E')]
   345→           bg-no-repeat bg-[right_1rem_center] bg-[length:1rem];
   346→  }
   347→
   348→  .label {
   349→    @apply text-sm font-medium text-foreground mb-2 block;
   350→  }
   351→}
   352→
   353→/* ───────────────────────────────────────────────────────────────────────────
   354→   Chat Styles
   355→   ─────────────────────────────────────────────────────────────────────────── */
   356→@layer components {
   357→  .chat-bubble {
   358→    @apply max-w-[80%] rounded-2xl px-4 py-3 animate-fade-in-up;
   359→  }
   360→
   361→  .chat-bubble-user {
