   396→
   397→/* ───────────────────────────────────────────────────────────────────────────
   398→   Insight Seal (印章)
   399→   ─────────────────────────────────────────────────────────────────────────── */
   400→@layer components {
   401→  .insight-seal {
   402→    @apply relative inline-flex items-center justify-center
   403→           w-12 h-12 rounded-full
   404→           text-antique-gold font-serif text-xs font-bold
   405→           border-2 border-antique-gold/30
   406→           bg-gradient-to-br from-antique-gold/10 to-transparent;
   407→    transform: rotate(-12deg);
   408→  }
   409→
   410→  .insight-seal::before {
   411→    content: "";
   412→    @apply absolute inset-1 rounded-full border border-antique-gold/20;
   413→  }
   414→
   415→  .insight-seal-lg {
   416→    @apply w-16 h-16 text-sm;
   417→  }
   418→}
   419→
   420→/* ───────────────────────────────────────────────────────────────────────────
   421→   Breath Aura (呼吸光晕)
   422→   ─────────────────────────────────────────────────────────────────────────── */
   423→@layer components {
   424→  .breath-aura {
   425→    @apply absolute rounded-full blur-3xl;
   426→    background: var(--skill-glow);
   427→    animation: breath var(--breath-duration) ease-in-out infinite;
   428→  }
   429→
   430→  .breath-aura-sm {
   431→    @apply w-32 h-32;
   432→  }
   433→
   434→  .breath-aura-md {
