   440→  }
   441→}
   442→
   443→/* ───────────────────────────────────────────────────────────────────────────
   444→   Vibe Glyph (品牌图标)
   445→   ─────────────────────────────────────────────────────────────────────────── */
   446→@layer components {
   447→  .vibe-glyph {
   448→    @apply relative flex items-center justify-center;
   449→  }
   450→
   451→  .vibe-glyph-inner {
   452→    @apply relative z-10;
   453→  }
   454→
   455→  .vibe-glyph-aura {
   456→    @apply absolute inset-0 breath-aura rounded-full;
   457→  }
   458→}
   459→
   460→/* ───────────────────────────────────────────────────────────────────────────
   461→   Navigation
   462→   ─────────────────────────────────────────────────────────────────────────── */
   463→@layer components {
   464→  .nav {
   465→    @apply glass sticky top-0 z-50 border-b border-border/50;
   466→  }
   467→
   468→  .nav-container {
   469→    @apply max-w-6xl mx-auto px-4 md:px-8 h-16 flex items-center justify-between;
   470→  }
   471→
   472→  .nav-logo {
   473→    @apply font-serif text-xl font-semibold text-foreground;
   474→  }
   475→
   476→  .nav-links {
