   482→  }
   483→}
   484→
   485→/* ───────────────────────────────────────────────────────────────────────────
   486→   Scrollbar
   487→   ─────────────────────────────────────────────────────────────────────────── */
   488→::-webkit-scrollbar {
   489→  width: 8px;
   490→  height: 8px;
   491→}
   492→
   493→::-webkit-scrollbar-track {
   494→  background: hsl(var(--muted) / 0.3);
   495→}
   496→
   497→::-webkit-scrollbar-thumb {
   498→  background: hsl(var(--muted-foreground) / 0.3);
   499→  border-radius: 4px;
   500→}
   501→
   502→::-webkit-scrollbar-thumb:hover {
   503→  background: hsl(var(--muted-foreground) / 0.5);
   504→}
   505→
   506→/* ───────────────────────────────────────────────────────────────────────────
   507→   Focus Styles
   508→   ─────────────────────────────────────────────────────────────────────────── */
   509→*:focus-visible {
   510→  @apply outline-none ring-2 ring-ring ring-offset-2 ring-offset-background;
   511→}
   512→
   513→/* ───────────────────────────────────────────────────────────────────────────
   514→   Utility Classes
   515→   ─────────────────────────────────────────────────────────────────────────── */
   516→@layer utilities {
   517→  .text-balance {
   518→    text-wrap: balance;
   519→  }
   520→
   521→  .animate-in {
   522→    animation: fadeIn 0.4s ease-out;
   523→  }
   524→
   525→  .slide-up {
   526→    animation: fadeInUp 0.4s ease-out;
   527→  }
   528→
   529→  /* Slow pulse animations for decorative rings */
   530→  .animate-pulse-slow {
   531→    animation: pulse-slow 3s ease-in-out infinite;
   532→  }
   533→
   534→  .animate-pulse-slower {
   535→    animation: pulse-slow 4s ease-in-out infinite;
   536→    animation-delay: 0.5s;
   537→  }
   538→
   539→  /* Line clamp utility */
   540→  .line-clamp-2 {
   541→    display: -webkit-box;
   542→    -webkit-line-clamp: 2;
   543→    -webkit-box-orient: vertical;
   544→    overflow: hidden;
   545→  }
   546→
   547→  /* Hero text style */
   548→  .hero-title {
   549→    @apply font-serif italic text-hero text-foreground;
   550→  }
   551→
   552→  .hero-subtitle {
   553→    @apply font-sans text-lg text-muted-foreground;
   554→  }
   555→
   556→  /* Section title style */
   557→  .section-title {
   558→    @apply font-serif text-section text-foreground;
   559→  }
   560→
   561→  /* Skill-aware text color */
   562→  .text-skill-primary {
   563→    color: hsl(var(--skill-primary));
   564→  }
   565→
   566→  .text-skill-secondary {
   567→    color: hsl(var(--skill-secondary));
   568→  }
   569→
   570→  .bg-skill-primary {
   571→    background-color: hsl(var(--skill-primary));
   572→  }
   573→
   574→  .bg-skill-secondary {
   575→    background-color: hsl(var(--skill-secondary));
   576→  }
   577→
   578→  .border-skill-primary {
   579→    border-color: hsl(var(--skill-primary));
   580→  }
   581→
   582→  /* Skill-aware shadow */
   583→  .shadow-skill-primary\/5 {
   584→    box-shadow: 0 4px 14px -3px hsl(var(--skill-primary) / 0.15);
   585→  }
   586→
   587→  /* From gradient utility */
   588→  .from-skill-primary\/5 {
   589→    --tw-gradient-from: hsl(var(--skill-primary) / 0.05);
   590→    --tw-gradient-to: transparent;
   591→    --tw-gradient-stops: var(--tw-gradient-from), var(--tw-gradient-to);
   592→  }
   593→}
   594→
   595→/* ───────────────────────────────────────────────────────────────────────────
   596→   Keyframe Animations
   597→   ─────────────────────────────────────────────────────────────────────────── */
   598→@keyframes fadeIn {
   599→  from { opacity: 0; }
   600→  to { opacity: 1; }
   601→}
   602→
   603→@keyframes fadeInUp {
   604→  from {
   605→    opacity: 0;
   606→    transform: translateY(10px);
   607→  }
   608→  to {
   609→    opacity: 1;
   610→    transform: translateY(0);
   611→  }
   612→}
   613→
   614→@keyframes breath {
   615→  0%, 100% {
   616→    opacity: 0.4;
   617→    transform: scale(1);
   618→  }
   619→  50% {
   620→    opacity: 0.55;  /* 修正: 原 0.7, 更微妙的变化 */
   621→    transform: scale(var(--breath-scale, 1.015));  /* 修正: 原 1.05 */
   622→  }
   623→}
   624→
   625→/* 墨水显影动画 - Ink Reveal */
   626→@keyframes ink-reveal {
   627→  from {
   628→    filter: blur(4px);
   629→    opacity: 0;
   630→  }
   631→  to {
   632→    filter: blur(0);
   633→    opacity: 1;
   634→  }
   635→}
   636→
   637→@keyframes pulse-slow {
   638→  0%, 100% {
   639→    opacity: 0.3;
   640→    transform: scale(1);
   641→  }
   642→  50% {
   643→    opacity: 0.6;
   644→    transform: scale(1.02);
   645→  }
   646→}
   647→
   648→@keyframes float {
   649→  0%, 100% {
   650→    transform: translateY(0);
   651→  }
   652→  50% {
   653→    transform: translateY(-8px);
   654→  }
   655→}
   656→
   657→/* ───────────────────────────────────────────────────────────────────────────
   658→   Ink Reveal Animation Classes
   659→   ─────────────────────────────────────────────────────────────────────────── */
   660→.ink-text {
   661→  animation: ink-reveal 0.6s var(--ease-out-expo) forwards;
   662→}
   663→
   664→.ink-text-delay-1 {
   665→  animation: ink-reveal 0.6s var(--ease-out-expo) 0.1s forwards;
