@layer utilities {
  /* Screen Reader Only - WCAG 2.1 Compliant */
  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border-width: 0;
  }

  .sr-only-focusable:focus {
    position: static;
    width: auto;
    height: auto;
    padding: inherit;
    margin: inherit;
    overflow: visible;
    clip: auto;
    white-space: normal;
  }

  .text-balance {
    text-wrap: balance;
  }

  .animate-in {
    animation: fadeIn 0.4s ease-out;
  }

  .slide-up {
    animation: fadeInUp 0.4s ease-out;
  }

