@tailwind base;
@tailwind components;
@tailwind utilities;

/* ═══════════════════════════════════════════════════════════════════════════
   LUMINOUS PAPER Design System - Global Styles
   ═══════════════════════════════════════════════════════════════════════════ */

/* ───────────────────────────────────────────────────────────────────────────
   CSS Variables - Light Mode (Default)
   ─────────────────────────────────────────────────────────────────────────── */
:root {
  /* Typography (runtime-loaded via <link>, with safe fallbacks) */
  --font-fraunces: "Fraunces", "Noto Serif SC", "Source Han Serif SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei", ui-serif, serif;
  --font-bricolage: "Bricolage Grotesque", "Noto Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei", -apple-system, BlinkMacSystemFont, ui-sans-serif, system-ui, sans-serif;

  /* Brand Colors */
  --ink: 28 26 23;
  --antique-gold: 184 138 68;
  --vellum: 251 247 241;

  /* Semantic Colors (HSL values) */
  --background: 40 33% 96%;        /* #FBF7F1 Vellum */
  --foreground: 30 10% 9%;         /* #1C1A17 Ink */
  --card: 0 0% 100%;               /* #FFFFFF Paper */
  --card-foreground: 30 10% 9%;
  --muted: 35 10% 92%;             /* #F6F0E7 Wash */
  --muted-foreground: 30 8% 39%;   /* #6D655B */
  --accent: 36 47% 49%;            /* #B88A44 Antique Gold */
  --accent-foreground: 0 0% 100%;
  --border: 35 20% 87%;            /* #E8E0D6 */
  --ring: 36 47% 49%;              /* Antique Gold */

  /* Skill Theme Variables (default: bazi) */
  --skill-primary: 24 65% 29%;     /* #7A3F1A */
  --skill-secondary: 36 47% 49%;   /* #B88A44 */
  --skill-glow: rgba(122, 63, 26, 0.15);
  --skill-bg: 40 100% 98%;         /* #FFFBF5 */

  /* Five Elements Glow Colors */
  --glow-wood: rgba(122, 63, 26, 0.10);
  --glow-fire: rgba(177, 60, 65, 0.10);
  --glow-earth: rgba(184, 138, 68, 0.10);
  --glow-metal: rgba(14, 116, 144, 0.08);
  --glow-water: rgba(60, 58, 122, 0.08);

  /* Animation Durations - Zen Breath (禅意呼吸) */
  --breath-duration: 16s;   /* 修正: 原 4s, 禅意呼吸 0.0625Hz */
  --float-duration: 20s;    /* 修正: 原 6s */
  --breath-scale: 1.015;    /* 修正: 原 1.05, 最大 1.02 */

  /* Easing Functions */
  --ease-out-expo: cubic-bezier(0.16, 1, 0.3, 1);
  --ease-spring: cubic-bezier(0.34, 1.56, 0.64, 1);
  --ease-smooth: cubic-bezier(0.4, 0, 0.2, 1);
}

/* ───────────────────────────────────────────────────────────────────────────
   CSS Variables - Dark Mode
   ─────────────────────────────────────────────────────────────────────────── */
.dark {
  --background: 30 7% 6%;          /* #11110F Warm Charcoal */
  --foreground: 35 20% 93%;        /* #F2EEE7 */
  --card: 30 7% 9%;                /* #181713 */
  --card-foreground: 35 20% 93%;
  --muted: 30 7% 12%;
  --muted-foreground: 30 8% 57%;   /* #9A9287 */
  --accent: 36 47% 49%;            /* Antique Gold stays same */
  --accent-foreground: 30 10% 9%;
  --border: 30 7% 16%;             /* #2A2822 */
  --ring: 36 47% 49%;

  /* Adjust glows for dark mode */
  --glow-wood: rgba(184, 138, 68, 0.15);
  --glow-fire: rgba(243, 166, 169, 0.12);
  --glow-earth: rgba(184, 138, 68, 0.15);
  --glow-metal: rgba(103, 232, 249, 0.10);
  --glow-water: rgba(167, 166, 244, 0.10);
}

/* ───────────────────────────────────────────────────────────────────────────
   Skill Theme Variants
   ─────────────────────────────────────────────────────────────────────────── */
[data-skill="bazi"] {
  --skill-primary: 24 65% 29%;
  --skill-secondary: 36 47% 49%;
  --skill-glow: rgba(122, 63, 26, 0.15);
  --skill-bg: 40 100% 98%;
}

[data-skill="zodiac"] {
  --skill-primary: 241 35% 35%;
  --skill-secondary: 241 83% 81%;
  --skill-glow: rgba(60, 58, 122, 0.12);
  --skill-bg: 240 100% 99%;
}

[data-skill="mbti"] {
  --skill-primary: 175 76% 26%;
  --skill-secondary: 160 76% 66%;
  --skill-glow: rgba(15, 118, 110, 0.12);
  --skill-bg: 160 83% 97%;
}

[data-skill="attach"] {
  --skill-primary: 358 50% 47%;
  --skill-secondary: 358 78% 81%;
  --skill-glow: rgba(177, 60, 65, 0.12);
  --skill-bg: 0 100% 98%;
}

[data-skill="career"] {
  --skill-primary: 193 77% 32%;
  --skill-secondary: 188 94% 69%;
  --skill-glow: rgba(14, 116, 144, 0.12);
  --skill-bg: 188 100% 97%;
}

/* ───────────────────────────────────────────────────────────────────────────
   Base Styles
   ─────────────────────────────────────────────────────────────────────────── */
@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  body {
    @apply bg-background text-foreground font-sans;
    min-height: 100vh;
  }

  /* Typography defaults */
  h1, h2, h3, h4, h5, h6 {
    @apply font-serif tracking-tight;
  }

  /* Selection */
  ::selection {
    @apply bg-accent/20 text-foreground;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Vellum Background (Luminous Paper)
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .vellum-bg {
    @apply relative bg-vellum;
  }

  .vellum-bg::before {
    content: "";
    @apply absolute inset-0 pointer-events-none;
    opacity: 0.55;
    background:
      radial-gradient(circle at 20% 10%, var(--glow-earth), transparent 45%),
      radial-gradient(circle at 80% 30%, var(--glow-water), transparent 50%),
      radial-gradient(circle at 50% 70%, var(--glow-wood), transparent 55%),
      radial-gradient(rgba(28, 26, 23, 0.03) 1px, transparent 1px);
    background-size: auto, auto, auto, 28px 28px;
    mix-blend-mode: multiply;
  }

  .dark .vellum-bg {
    @apply bg-background;
  }

  .dark .vellum-bg::before {
    opacity: 0.3;
    mix-blend-mode: screen;
  }

  /* Luminous Paper with Skill glow */
  .luminous-paper {
    @apply relative;
    background: hsl(var(--skill-bg));
  }

  .luminous-paper::before {
    content: "";
    @apply absolute inset-0 pointer-events-none;
    background: radial-gradient(
      ellipse 80% 50% at 50% 0%,
      var(--skill-glow),
      transparent 70%
    );
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Glass Effects (Glassmorphism)
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .glass {
    @apply backdrop-blur-xl;
    background: hsl(var(--background) / 0.7);
    border: 1px solid hsl(var(--border) / 0.5);
  }

  .glass-card {
    @apply backdrop-blur-lg rounded-xl;
    background: hsl(var(--card) / 0.8);
    border: 1px solid hsl(var(--border) / 0.3);
    box-shadow:
      0 4px 24px rgba(28, 26, 23, 0.06),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  .glass-card:hover {
    box-shadow:
      0 8px 32px rgba(28, 26, 23, 0.1),
      inset 0 1px 0 rgba(255, 255, 255, 0.15);
  }

  .glass-subtle {
    @apply backdrop-blur-md;
    background: hsl(var(--background) / 0.85);
  }

  /* Capsule (Omnibar style) */
  .glass-capsule {
    @apply backdrop-blur-xl rounded-full;
    background: hsl(var(--card) / 0.9);
    border: 1px solid hsl(var(--border) / 0.4);
    box-shadow:
      0 4px 20px rgba(28, 26, 23, 0.08),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Button Styles
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .btn {
    @apply inline-flex items-center justify-center gap-2 font-sans font-medium
           transition-all duration-200 ease-out-expo
           focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2
           disabled:pointer-events-none disabled:opacity-50;
  }

  .btn-primary {
    @apply btn bg-ink text-vellum rounded-full px-6 py-3
           hover:bg-ink/90 active:scale-[0.98];
  }

  .btn-secondary {
    @apply btn bg-card text-foreground border border-border rounded-full px-6 py-3
           hover:bg-muted active:scale-[0.98];
  }

  .btn-ghost {
    @apply btn text-foreground rounded-lg px-4 py-2
           hover:bg-muted/50 active:scale-[0.98];
  }

  .btn-gold {
    @apply btn bg-antique-gold text-white rounded-full px-6 py-3
           hover:bg-antique-gold/90 active:scale-[0.98]
           shadow-glow-gold;
  }

  .btn-sm {
    @apply text-sm px-4 py-2;
  }

  .btn-lg {
    @apply text-lg px-8 py-4;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Card Styles
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .card {
    @apply bg-card text-card-foreground rounded-xl border border-border
           shadow-card transition-shadow duration-200;
  }

  .card:hover {
    @apply shadow-card-hover;
  }

  .card-header {
    @apply p-6 pb-0;
  }

  .card-title {
    @apply font-serif text-card-title font-semibold tracking-tight;
  }

  .card-content {
    @apply p-6;
  }

  /* Insight Card */
  .insight-card {
    @apply card border-l-4 shadow-insight;
    border-left-color: hsl(var(--skill-primary));
  }

  .insight-card[data-type="discovery"] {
    @apply border-l-element-water;
  }

  .insight-card[data-type="pattern"] {
    @apply border-l-zodiac-primary;
  }

  .insight-card[data-type="timing"] {
    @apply border-l-antique-gold;
  }

  .insight-card[data-type="growth"] {
    @apply border-l-element-wood;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Input Styles
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .input {
    @apply w-full bg-card text-foreground rounded-lg px-4 py-3
           border border-border
           placeholder:text-muted-foreground
           focus:outline-none focus:ring-2 focus:ring-ring focus:border-transparent
           transition-all duration-200;
  }

  .input-capsule {
    @apply input rounded-full px-6;
  }

  .select {
    @apply input appearance-none cursor-pointer
           bg-[url('data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%2224%22%20height%3D%2224%22%20viewBox%3D%220%200%2024%2024%22%20fill%3D%22none%22%20stroke%3D%22%236D655B%22%20stroke-width%3D%222%22%20stroke-linecap%3D%22round%22%20stroke-linejoin%3D%22round%22%3E%3Cpath%20d%3D%22m6%209%206%206%206-6%22%2F%3E%3C%2Fsvg%3E')]
           bg-no-repeat bg-[right_1rem_center] bg-[length:1rem];
  }

  .label {
    @apply text-sm font-medium text-foreground mb-2 block;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Chat Styles
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .chat-bubble {
    @apply max-w-[80%] rounded-2xl px-4 py-3 animate-fade-in-up;
  }

  .chat-bubble-user {
    @apply chat-bubble bg-ink text-vellum ml-auto rounded-br-sm;
  }

  .chat-bubble-assistant {
    @apply chat-bubble bg-card border border-border mr-auto rounded-bl-sm;
  }

  .chat-timestamp {
    @apply text-xs text-muted-foreground mt-1;
  }

  /* Omnibar */
  .omnibar {
    @apply glass-capsule flex items-center gap-3 px-4 py-3;
  }

  .omnibar-input {
    @apply flex-1 bg-transparent border-none outline-none
           text-foreground placeholder:text-muted-foreground
           resize-none;
  }

  .omnibar-btn {
    @apply p-2 rounded-full text-muted-foreground
           hover:bg-muted/50 hover:text-foreground
           transition-colors duration-200;
  }

  .omnibar-send {
    @apply p-2 rounded-full bg-ink text-vellum
           hover:bg-ink/90 active:scale-95
           transition-all duration-200;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Insight Seal (印章)
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .insight-seal {
    @apply relative inline-flex items-center justify-center
           w-12 h-12 rounded-full
           text-antique-gold font-serif text-xs font-bold
           border-2 border-antique-gold/30
           bg-gradient-to-br from-antique-gold/10 to-transparent;
    transform: rotate(-12deg);
  }

  .insight-seal::before {
    content: "";
    @apply absolute inset-1 rounded-full border border-antique-gold/20;
  }

  .insight-seal-lg {
    @apply w-16 h-16 text-sm;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Breath Aura (呼吸光晕)
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .breath-aura {
    @apply absolute rounded-full blur-3xl;
    background: var(--skill-glow);
    animation: breath var(--breath-duration) ease-in-out infinite;
  }

  .breath-aura-sm {
    @apply w-32 h-32;
  }

  .breath-aura-md {
    @apply w-48 h-48;
  }

  .breath-aura-lg {
    @apply w-64 h-64;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Vibe Glyph (品牌图标)
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .vibe-glyph {
    @apply relative flex items-center justify-center;
  }

  .vibe-glyph-inner {
    @apply relative z-10;
  }

  .vibe-glyph-aura {
    @apply absolute inset-0 breath-aura rounded-full;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Navigation
   ─────────────────────────────────────────────────────────────────────────── */
@layer components {
  .nav {
    @apply glass sticky top-0 z-50 border-b border-border/50;
  }

  .nav-container {
    @apply max-w-6xl mx-auto px-4 md:px-8 h-16 flex items-center justify-between;
  }

  .nav-logo {
    @apply font-serif text-xl font-semibold text-foreground;
  }

  .nav-links {
    @apply hidden md:flex items-center gap-6;
  }

  .nav-link {
    @apply text-sm text-muted-foreground hover:text-foreground transition-colors;
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Scrollbar
   ─────────────────────────────────────────────────────────────────────────── */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--muted) / 0.3);
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--muted-foreground) / 0.3);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground) / 0.5);
}

/* ───────────────────────────────────────────────────────────────────────────
   Focus Styles
   ─────────────────────────────────────────────────────────────────────────── */
*:focus-visible {
  @apply outline-none ring-2 ring-ring ring-offset-2 ring-offset-background;
}

/* ───────────────────────────────────────────────────────────────────────────
   Utility Classes
   ─────────────────────────────────────────────────────────────────────────── */
@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .animate-in {
    animation: fadeIn 0.4s ease-out;
  }

  .slide-up {
    animation: fadeInUp 0.4s ease-out;
  }

  /* Slow pulse animations for decorative rings */
  .animate-pulse-slow {
    animation: pulse-slow 3s ease-in-out infinite;
  }

  .animate-pulse-slower {
    animation: pulse-slow 4s ease-in-out infinite;
    animation-delay: 0.5s;
  }

  /* Line clamp utility */
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Hero text style */
  .hero-title {
    @apply font-serif italic text-hero text-foreground;
  }

  .hero-subtitle {
    @apply font-sans text-lg text-muted-foreground;
  }

  /* Section title style */
  .section-title {
    @apply font-serif text-section text-foreground;
  }

  /* Skill-aware text color */
  .text-skill-primary {
    color: hsl(var(--skill-primary));
  }

  .text-skill-secondary {
    color: hsl(var(--skill-secondary));
  }

  .bg-skill-primary {
    background-color: hsl(var(--skill-primary));
  }

  .bg-skill-secondary {
    background-color: hsl(var(--skill-secondary));
  }

  .border-skill-primary {
    border-color: hsl(var(--skill-primary));
  }

  /* Skill-aware shadow */
  .shadow-skill-primary\/5 {
    box-shadow: 0 4px 14px -3px hsl(var(--skill-primary) / 0.15);
  }

  /* From gradient utility */
  .from-skill-primary\/5 {
    --tw-gradient-from: hsl(var(--skill-primary) / 0.05);
    --tw-gradient-to: transparent;
    --tw-gradient-stops: var(--tw-gradient-from), var(--tw-gradient-to);
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Keyframe Animations
   ─────────────────────────────────────────────────────────────────────────── */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes breath {
  0%, 100% {
    opacity: 0.4;
    transform: scale(1);
  }
  50% {
    opacity: 0.55;  /* 修正: 原 0.7, 更微妙的变化 */
    transform: scale(var(--breath-scale, 1.015));  /* 修正: 原 1.05 */
  }
}

/* 墨水显影动画 - Ink Reveal */
@keyframes ink-reveal {
  from {
    filter: blur(4px);
    opacity: 0;
  }
  to {
    filter: blur(0);
    opacity: 1;
  }
}

@keyframes pulse-slow {
  0%, 100% {
    opacity: 0.3;
    transform: scale(1);
  }
  50% {
    opacity: 0.6;
    transform: scale(1.02);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-8px);
  }
}

/* ───────────────────────────────────────────────────────────────────────────
   Ink Reveal Animation Classes
   ─────────────────────────────────────────────────────────────────────────── */
.ink-text {
  animation: ink-reveal 0.6s var(--ease-out-expo) forwards;
}

.ink-text-delay-1 {
  animation: ink-reveal 0.6s var(--ease-out-expo) 0.1s forwards;
  opacity: 0;
}

.ink-text-delay-2 {
  animation: ink-reveal 0.6s var(--ease-out-expo) 0.2s forwards;
  opacity: 0;
}

.ink-text-delay-3 {
  animation: ink-reveal 0.6s var(--ease-out-expo) 0.3s forwards;
  opacity: 0;
}

/* ───────────────────────────────────────────────────────────────────────────
   Zen Breath - 默认静止原则
   核心: 默认静止，交互时苏醒
   ─────────────────────────────────────────────────────────────────────────── */

/* 光晕默认静止 */
.breath-aura {
  animation-play-state: paused;
}

/* 交互时激活 */
.interactive-zone:hover .breath-aura,
.interactive-zone:focus-within .breath-aura,
[data-active="true"] .breath-aura,
[data-breathing="true"] .breath-aura {
  animation-play-state: running;
}

/* 推演/加载状态时持续呼吸 */
[data-state="processing"] .breath-aura,
[data-state="loading"] .breath-aura,
.always-breathing .breath-aura {
  animation-play-state: running;
}

/* Hero 区域始终保持轻微呼吸 */
.hero-zone .breath-aura {
  animation-play-state: running;
}

