/**
 * Journey é¡µé¢çŠ¶æ€ - 4 çŠ¶æ€æ¸è¿›ç³»ç»Ÿ
 * - empty: æœªè®¾ç½®åŒ—ææ˜Ÿï¼Œå¼•å¯¼å¼€å§‹
 * - north_star_set: æœ‰åŒ—ææ˜Ÿï¼Œæ— è·¯çº¿å›¾ï¼Œå¼•å¯¼åˆ¶å®šè·¯çº¿å›¾
 * - planned: æœ‰è·¯çº¿å›¾ï¼Œæ— å‘¨å¤§çŸ³å¤´ï¼Œå¼•å¯¼è®¾å®šæœ¬å‘¨å¤§çŸ³å¤´
 * - executing: æœ‰å‘¨å¤§çŸ³å¤´ï¼Œå±•ç¤ºå®Œæ•´æ‰§è¡Œä»ªè¡¨ç›˜
 */
export type JourneyState = "empty" | "north_star_set" | "planned" | "executing";

/**
 * æ ¹æ® skill_data åˆ¤æ–­é¡µé¢çŠ¶æ€
 */
export function getJourneyState(data: LifecoachSkillData | null | undefined): JourneyState {
  // State 0: empty - æ²¡æœ‰åŒ—ææ˜Ÿæ„¿æ™¯
  if (!data?.north_star?.vision_scene) {
    return "empty";
  }

  // State 1: north_star_set - æœ‰åŒ—ææ˜Ÿï¼Œä½†æ²¡æœ‰è·¯çº¿å›¾/å¹´åº¦ç›®æ ‡
  const hasRoadmap = (data.roadmap?.goals?.length ?? 0) > 0 || (data.goals?.length ?? 0) > 0;
  if (!hasRoadmap) {
    return "north_star_set";
  }

  // State 2: planned - æœ‰è·¯çº¿å›¾ï¼Œä½†æ²¡æœ‰å‘¨å¤§çŸ³å¤´
  const hasWeeklyRocks = (data.weekly?.rocks?.length ?? 0) > 0 || (data.weekly_rocks?.rocks?.length ?? 0) > 0;
  if (!hasWeeklyRocks) {
    return "planned";
  }

  // State 3: executing - å®Œæ•´æ‰§è¡ŒçŠ¶æ€
  return "executing";
}

