// Chat API
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€

export async function sendMessage(request: ChatRequest): Promise<ChatResponse> {
  const response = await fetchAPI("/chat/", {
    method: "POST",
    body: JSON.stringify(request),
  });

  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.detail || "Chat failed");
  }

  return response.json();
}

export async function sendGuestMessage(
  message: string,
  skill: string
): Promise<{ content: string; is_guest: boolean; suggestion: string }> {
  const response = await fetch("/api/proxy?endpoint=/api/v1/chat/guest", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ message, skill }),
  });

  if (!response.ok) {
    throw new Error("Chat failed");
  }

  return response.json();
}

export interface StreamChatResult {
  conversation_id?: string;
  skill?: string;
  voice_mode?: string;
}

export async function streamChat(
  request: ChatRequest,
  onChunk: (chunk: string) => void,
  onDone: (result: StreamChatResult) => void,
  onError: (error: Error) => void
): Promise<void> {
  const { accessToken } = getTokens();

  try {
    const response = await fetch("/api/chat", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...(accessToken && { Authorization: `Bearer ${accessToken}` }),
      },
      body: JSON.stringify(request),
    });

    if (!response.ok) {
      const error = await response.json().catch(() => ({ detail: "Stream failed" }));
      onError(new Error(error.detail || "Stream failed"));
      return;
    }

    const reader = response.body?.getReader();
    const decoder = new TextDecoder();

    if (!reader) {
      onError(new Error("No response body"));
      return;
    }

    let buffer = "";

    while (true) {
      const { done, value } = await reader.read();
      if (done) break;

      buffer += decoder.decode(value, { stream: true });
      const lines = buffer.split("\n");
      buffer = lines.pop() || "";

