// äº”è¡Œç±»å‹
type Element = "wood" | "fire" | "earth" | "metal" | "water";

// å¤©å¹²åœ°æ”¯æ•°æ®
interface Pillar {
  stem: string;
  branch: string;
  element: Element;
  tenGod?: string;
}

interface BaziData {
  year: Pillar;
