  /** Kçº¿ç®€è¦æ•°æ® */
  klinePreview?: {
    currentScore: number;
    trend: "up" | "down" | "stable";
    period: string;
  };
  /** å®Œæ•´ K çº¿æ•°æ® */
  klineData?: FortuneDataPoint[];
  /** Identity Prism æ•°æ® */
  prismContent?: PrismContent;
  /** æ˜¯å¦æœ‰å®Œæ•´æŠ¥å‘Š */
  hasReport?: boolean;
  /** æ˜¯å¦å±•å¼€ K çº¿ */
