    identityLabel: "ä¾æ‹èº«ä»½è§†è§’",
  },
  career: {
    klineLabel: "èŒä¸šè½¨è¿¹",
    reportLabel: "èŒä¸šå‘å±•æŠ¥å‘Š",
    identityLabel: "èŒä¸šèº«ä»½è§†è§’",
  },
  lifecoach: {
    klineLabel: "æˆé•¿è½¨è¿¹",
    reportLabel: "äººç”Ÿè“å›¾æŠ¥å‘Š",
    identityLabel: "æ•™ç»ƒèº«ä»½è§†è§’",
  },
};

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Sub-components
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function NowNextCard({ data }: { data: ChartPanelProps["nowNextData"] }) {
  if (!data) {
    return (
      <div className="bg-bg-card border border-subtle shadow-soft rounded-xl p-4 text-center text-muted-foreground text-sm">
        å®Œæˆæ›´å¤šå¯¹è¯åï¼Œè¿™é‡Œä¼šæ˜¾ç¤ºä½ çš„å½“å‰é˜¶æ®µå’Œä¸‹ä¸€æ­¥å»ºè®®
      </div>
    );
  }

  return <NowNextCardComponent now={data.now} next={data.next} />;
}

function KLinePreviewCard({
  data,
  label,
  onClick,
}: {
  data?: ChartPanelProps["klinePreview"];
  label: string;
  onClick?: () => void;
}) {
  const getTrendIcon = () => {
    if (!data) return null;
    switch (data.trend) {
      case "up":
        return <TrendingUp className="w-4 h-4 text-emerald-500" />;
      case "down":
        return <TrendingUp className="w-4 h-4 text-red-500 rotate-180" />;
      default:
        return <div className="w-4 h-0.5 bg-muted-foreground" />;
    }
  };

  return (
    <button
      onClick={onClick}
      disabled={!onClick}
      className={cn(
        "w-full bg-bg-card border border-subtle shadow-soft rounded-xl p-4 text-left transition-all group",
        onClick ? "hover:shadow-md" : "opacity-60 cursor-not-allowed"
      )}
    >
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <Calendar className="w-4 h-4 text-skill-primary" />
          <span className="text-sm font-medium text-foreground">{label}</span>
