"use client";

import React, { useRef, useState, useCallback, useEffect } from "react";
import { cn } from "@/lib/utils";

interface CarouselTrackProps {
  children: React.ReactNode;
  gap?: number;
  padding?: number;
  className?: string;
}

/**
 * CarouselTrack - æ¨ªæ»‘è½¨é“ç»„ä»¶
 *
 * å®ç°æ–¹å¼:
 * - CSS scroll-snap for native smooth scrolling
 * - Touch/mouse drag support
 * - Hidden scrollbar
 */
export function CarouselTrack({
  children,
  gap = 16,
  padding = 16,
  className,
}: CarouselTrackProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemCount = React.Children.count(children);

  // Update current index on scroll
  const handleScroll = useCallback(() => {
    if (!containerRef.current) return;

    const container = containerRef.current;
    const scrollLeft = container.scrollLeft;
    const itemWidth = container.firstElementChild?.clientWidth || 0;
    const index = Math.round(scrollLeft / (itemWidth + gap));
    setCurrentIndex(Math.min(index, itemCount - 1));
  }, [gap, itemCount]);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    container.addEventListener("scroll", handleScroll, { passive: true });
    return () => container.removeEventListener("scroll", handleScroll);
  }, [handleScroll]);

  return (
    <div className="relative">
      {/* Track */}
      <div
        ref={containerRef}
        className={cn(
          "overflow-x-auto scrollbar-hide",
          "snap-x snap-mandatory",
          "-mx-4 px-4",
          className
        )}
        style={{
          scrollPaddingLeft: padding,
          scrollPaddingRight: padding,
        }}
      >
        <div className="flex" style={{ gap }}>
          {React.Children.map(children, (child, index) => (
            <div key={index} className="flex-shrink-0 snap-start">
              {child}
            </div>
          ))}
        </div>
      </div>

      {/* Indicators */}
      {itemCount > 1 && (
        <CarouselIndicator current={currentIndex} total={itemCount} />
      )}
    </div>
  );
}

interface CarouselIndicatorProps {
  current: number;
  total: number;
  className?: string;
}

function CarouselIndicator({ current, total, className }: CarouselIndicatorProps) {
  // Only show up to 5 dots
  const maxDots = Math.min(total, 5);
  const dots = Array.from({ length: maxDots });

  return (
    <div
      className={cn(
        "flex items-center justify-center gap-1.5 mt-3",
        className
      )}
    >
      {dots.map((_, index) => (
        <span
          key={index}
          className={cn(
            "w-1.5 h-1.5 rounded-full transition-colors",
            index === current % maxDots ? "bg-gray-800" : "bg-gray-300"
          )}
        />
      ))}
    </div>
  );
}

export default CarouselTrack;

