'use client';

/**
 * CategorySection - åˆ†ç±»åŒºå—
 *
 * App Store é£æ ¼çš„æ¨ªå‘æ»šåŠ¨å¡ç‰‡åˆ—è¡¨
 */

import React, { useRef, useState, useCallback } from 'react';
import { ChevronRight } from 'lucide-react';
import { SkillShowcaseCard } from './SkillShowcaseCard';
import type { SkillMetadata, SkillUserStatus } from '@/types/skill';

interface CategorySectionProps {
  title: string;
  subtitle?: string;
  skills: SkillMetadata[];
  userStatuses?: Record<string, SkillUserStatus>;
  onSkillClick: (skillId: string) => void;
  onSubscribe: (skillId: string) => void;
  onViewAll?: () => void;
}

export function CategorySection({
  title,
  subtitle,
  skills,
  userStatuses = {},
  onSkillClick,
  onSubscribe,
  onViewAll,
}: CategorySectionProps) {
  const scrollRef = useRef<HTMLDivElement>(null);
  const [isDragging, setIsDragging] = useState(false);
  const [hasDragged, setHasDragged] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);

  // é¼ æ ‡æŒ‰ä¸‹å¼€å§‹æ‹–æ‹½
  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    if (!scrollRef.current) return;
    setIsDragging(true);
    setHasDragged(false);
    setStartX(e.pageX - scrollRef.current.offsetLeft);
    setScrollLeft(scrollRef.current.scrollLeft);
  }, []);

  // é¼ æ ‡ç§»åŠ¨æ‹–æ‹½
  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (!isDragging || !scrollRef.current) return;
    e.preventDefault();
    const x = e.pageX - scrollRef.current.offsetLeft;
    const walk = (x - startX) * 2; // æ»šåŠ¨é€Ÿåº¦å€æ•°

    // å¦‚æœç§»åŠ¨è·ç¦»è¶…è¿‡5pxï¼Œè®¤ä¸ºæ˜¯æ‹–æ‹½è€Œéç‚¹å‡»
    if (Math.abs(walk) > 5) {
      setHasDragged(true);
    }

    scrollRef.current.scrollLeft = scrollLeft - walk;
  }, [isDragging, startX, scrollLeft]);

  // é¼ æ ‡æŠ¬èµ·åœæ­¢æ‹–æ‹½
  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  // é¼ æ ‡ç¦»å¼€å®¹å™¨åœæ­¢æ‹–æ‹½
  const handleMouseLeave = useCallback(() => {
    setIsDragging(false);
  }, []);

  if (skills.length === 0) return null;

  return (
    <section className="space-y-4">
      {/* Header */}
      <div className="flex items-end justify-between">
        <div>
          <h2 className="text-2xl font-bold text-text-primary">{title}</h2>
          {subtitle && (
            <p className="text-sm text-text-secondary mt-1">{subtitle}</p>
          )}
        </div>
        {onViewAll && skills.length > 3 && (
          <button
            onClick={onViewAll}
            className="flex items-center gap-1 text-sm font-medium text-accent-primary hover:gap-2 transition-all"
          >
            <span>æŸ¥çœ‹å…¨éƒ¨</span>
            <ChevronRight className="w-4 h-4" />
          </button>
        )}
      </div>

