'use client';

/**
 * FeaturedSkillBanner - App Store é£æ ¼çš„ç²¾é€‰ Skill æ¨ªå¹…
 *
 * å¤§å°ºå¯¸å±•ç¤ºç²¾é€‰ Skillï¼Œå¸¦æ¸å˜èƒŒæ™¯å’Œè§†è§‰å†²å‡»åŠ›
 */

import React from 'react';
import { ArrowRight } from 'lucide-react';
import type { SkillMetadata } from '@/types/skill';

interface FeaturedSkillBannerProps {
  skill: SkillMetadata;
  campaign?: {
    title: string;
    subtitle?: string;
    badge?: string;
  };
  onClick: () => void;
}

export function FeaturedSkillBanner({ skill, campaign, onClick }: FeaturedSkillBannerProps) {
  return (
    <div
      onClick={onClick}
      className="relative overflow-hidden rounded-3xl cursor-pointer group h-[400px] lg:h-[480px]"
      style={{
        background: `linear-gradient(135deg, ${skill.color}15 0%, ${skill.color}05 50%, transparent 100%)`,
      }}
    >
      {/* èƒŒæ™¯è£…é¥° */}
      <div
        className="absolute inset-0 opacity-30"
        style={{
          backgroundImage: `radial-gradient(circle at 80% 20%, ${skill.color}40 0%, transparent 50%)`,
        }}
      />

      {/* å†…å®¹åŒº */}
      <div className="relative h-full flex flex-col justify-between p-8 lg:p-12">
        {/* é¡¶éƒ¨æ ‡ç­¾ */}
        {campaign?.badge && (
          <div className="inline-flex items-center gap-2 px-3 py-1.5 rounded-full bg-bg-card/80 backdrop-blur-sm w-fit">
            <span className="w-2 h-2 rounded-full bg-accent-primary animate-pulse" />
            <span className="text-sm font-medium text-text-primary">{campaign.badge}</span>
          </div>
        )}

        {/* ä¸»å†…å®¹ */}
        <div className="space-y-6">
          {/* å›¾æ ‡ */}
          <div
            className="w-24 h-24 lg:w-32 lg:h-32 rounded-3xl flex items-center justify-center text-6xl lg:text-7xl shadow-2xl transition-transform group-hover:scale-105"
            style={{
              backgroundColor: `${skill.color}`,
              boxShadow: `0 20px 60px ${skill.color}40`,
            }}
          >
            {skill.icon}
          </div>

          {/* æ–‡æ¡ˆ */}
          <div className="space-y-3 max-w-2xl">
            {campaign?.title && (
              <div className="text-sm font-medium text-accent-primary uppercase tracking-wider">
                {campaign.title}
              </div>
            )}
            <h2 className="font-serif text-4xl lg:text-5xl font-bold text-text-primary">
              {skill.name}
            </h2>
            {campaign?.subtitle && (
              <p className="text-lg lg:text-xl text-text-secondary font-medium">
                {campaign.subtitle}
              </p>
            )}
            <p className="text-base text-text-secondary">
              {skill.showcase?.tagline || skill.description}
            </p>
          </div>

          {/* CTA */}
          <button className="inline-flex items-center gap-2 px-6 py-3 rounded-full bg-accent-primary text-text-inverse font-medium hover:bg-accent-primary/90 transition-all hover:gap-3 group/btn">
            <span>ç«‹å³æ¢ç´¢</span>
            <ArrowRight className="w-4 h-4 transition-transform group-hover/btn:translate-x-1" />
          </button>
        </div>
      </div>

      {/* è£…é¥°æ€§èƒŒæ™¯å›¾æ¡ˆ */}
      <div className="absolute right-0 bottom-0 w-1/2 h-1/2 opacity-10 pointer-events-none">
        <div
          className="w-full h-full"
          style={{
            backgroundImage: `radial-gradient(circle, ${skill.color} 1px, transparent 1px)`,
            backgroundSize: '24px 24px',
          }}
        />
      </div>
