export default async function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  // ä» cookie è·å– skillï¼ˆç”± middleware è®¾ç½®ï¼‰
  const cookieStore = await cookies();
  const skillCookie = cookieStore.get("vibelife-skill");
  const initialSkill = (skillCookie?.value as SkillType) || "bazi";

  return (
    <html
      lang="zh-CN"
      suppressHydrationWarning
    >
      <head>
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="anonymous" />
        <link
          href="https://fonts.googleapis.com/css2?family=Fraunces:ital,wght@0,400;0,500;0,600;0,700;1,400;1,500;1,600;1,700&family=Bricolage+Grotesque:wght@400;500;600;700&display=swap"
          rel="stylesheet"
        />
      </head>
      <body
        className="font-sans antialiased"
        style={{
          fontFamily: 'var(--font-bricolage), "Noto Sans SC", "PingFang SC", "Hiragino Sans GB", "Microsoft YaHei", -apple-system, BlinkMacSystemFont, system-ui, sans-serif'
        }}
      >
        <Providers initialSkill={initialSkill}>
          {children}
        </Providers>
      </body>
    </html>
  );
}

