interface InsightData {
  id: string;
  insight_type: InsightType;
  title: string;
  content: string;
}

// Tool invocation type for Generative UI
interface ToolInvocation {
  toolCallId: string;
  toolName: string;
  args: Record<string, unknown>;
  state: "partial-call" | "call" | "result";
  result?: unknown;
}

/**
 * Extract text content from AI SDK Message
 * Handles both old (content string) and new (parts array) formats
 */
function getMessageContent(message: UIMessage): string {
  // First try direct content property (ai/react format)
  const directContent = (message as unknown as { content?: string }).content;
  if (directContent) {
    return directContent;
  }

  // Then try parts array (@ai-sdk/react format)
  if (message.parts && message.parts.length > 0) {
    return message.parts
      .filter((part): part is { type: 'text'; text: string } => part.type === 'text')
      .map(part => part.text)
      .join('');
  }

  return '';
}

/**
 * Extract tool invocations from AI SDK Message
 * AI SDK 6 uses parts with type starting with 'tool-' for tool invocations
 */
function getToolInvocations(message: UIMessage): ToolInvocation[] | undefined {
  // Try parts array for tool invocations
  if (message.parts && message.parts.length > 0) {
    const toolInvocations: ToolInvocation[] = [];

    for (const part of message.parts) {
      // AI SDK 6 tool parts have type starting with 'tool-'
      if (part.type.startsWith('tool-')) {
        const toolPart = part as unknown as {
          type: string;
          toolCallId: string;
          state?: string;
          input?: unknown;
          output?: unknown;
        };

        // Extract tool name from type (e.g., 'tool-show_bazi_chart' -> 'show_bazi_chart')
        const toolName = part.type.replace(/^tool-/, '');

        // Determine invocation state
        let state: ToolInvocation['state'] = 'call';
        if (toolPart.state === 'input-streaming' || toolPart.state === 'partial-call') {
          state = 'partial-call';
        } else if (toolPart.output !== undefined || toolPart.state === 'output-available') {
          state = 'result';
        }

        toolInvocations.push({
          toolCallId: toolPart.toolCallId || `tool-${Date.now()}`,
          toolName,
          args: (toolPart.input || {}) as Record<string, unknown>,
          state,
          result: toolPart.output,
        });
      }
    }

    if (toolInvocations.length > 0) {
      return toolInvocations;
    }
  }

  // Try direct toolInvocations property (legacy format)
  const directInvocations = (message as unknown as { toolInvocations?: ToolInvocation[] }).toolInvocations;
  if (directInvocations && directInvocations.length > 0) {
    return directInvocations;
  }

  return undefined;
}

