   Skill Theme Variants
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
[data-skill="bazi"] {
  --skill-primary: 24 65% 29%;
  --skill-secondary: 36 47% 49%;
  --skill-glow: rgba(122, 63, 26, 0.15);
  --skill-bg: 40 100% 98%;
}

[data-skill="zodiac"] {
  --skill-primary: 241 35% 35%;
  --skill-secondary: 241 83% 81%;
  --skill-glow: rgba(60, 58, 122, 0.12);
  --skill-bg: 240 100% 99%;
}

[data-skill="mbti"] {
  --skill-primary: 175 76% 26%;
  --skill-secondary: 160 76% 66%;
  --skill-glow: rgba(15, 118, 110, 0.12);
  --skill-bg: 160 83% 97%;
}

[data-skill="attach"] {
  --skill-primary: 358 50% 47%;
  --skill-secondary: 358 78% 81%;
  --skill-glow: rgba(177, 60, 65, 0.12);
  --skill-bg: 0 100% 98%;
}

[data-skill="career"] {
  --skill-primary: 193 77% 32%;
  --skill-secondary: 188 94% 69%;
  --skill-glow: rgba(14, 116, 144, 0.12);
  --skill-bg: 188 100% 97%;
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Base Styles
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  body {
    @apply bg-background text-foreground font-sans;
    min-height: 100vh;
  }

  /* Typography defaults */
  h1, h2, h3, h4, h5, h6 {
    @apply font-serif tracking-tight;
  }

  /* Selection */
  ::selection {
    @apply bg-accent/20 text-foreground;
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Vellum Background (Luminous Paper)
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .vellum-bg {
    @apply relative bg-vellum;
  }

  .vellum-bg::before {
    content: "";
    @apply absolute inset-0 pointer-events-none;
    opacity: 0.55;
    background:
      radial-gradient(circle at 20% 10%, var(--glow-earth), transparent 45%),
      radial-gradient(circle at 80% 30%, var(--glow-water), transparent 50%),
      radial-gradient(circle at 50% 70%, var(--glow-wood), transparent 55%),
      radial-gradient(rgba(28, 26, 23, 0.03) 1px, transparent 1px);
    background-size: auto, auto, auto, 28px 28px;
    mix-blend-mode: multiply;
  }

  .dark .vellum-bg {
    @apply bg-background;
  }

  .dark .vellum-bg::before {
    opacity: 0.3;
    mix-blend-mode: screen;
  }

  /* Luminous Paper with Skill glow */
  .luminous-paper {
    @apply relative;
    background: hsl(var(--skill-bg));
  }

  .luminous-paper::before {
    content: "";
    @apply absolute inset-0 pointer-events-none;
    background: radial-gradient(
      ellipse 80% 50% at 50% 0%,
      var(--skill-glow),
      transparent 70%
    );
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Glass Effects (Glassmorphism)
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .glass {
    @apply backdrop-blur-xl;
    background: hsl(var(--background) / 0.7);
    border: 1px solid hsl(var(--border) / 0.5);
  }

  .glass-card {
    @apply backdrop-blur-lg rounded-xl;
    background: hsl(var(--card) / 0.8);
    border: 1px solid hsl(var(--border) / 0.3);
    box-shadow:
      0 4px 24px rgba(28, 26, 23, 0.06),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  .glass-card:hover {
    box-shadow:
      0 8px 32px rgba(28, 26, 23, 0.1),
      inset 0 1px 0 rgba(255, 255, 255, 0.15);
  }

  .glass-subtle {
    @apply backdrop-blur-md;
    background: hsl(var(--background) / 0.85);
  }

  /* Capsule (Omnibar style) */
  .glass-capsule {
    @apply backdrop-blur-xl rounded-full;
    background: hsl(var(--card) / 0.9);
    border: 1px solid hsl(var(--border) / 0.4);
    box-shadow:
      0 4px 20px rgba(28, 26, 23, 0.08),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Button Styles
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .btn {
    @apply inline-flex items-center justify-center gap-2 font-sans font-medium
           transition-all duration-200 ease-out-expo
           focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2
           disabled:pointer-events-none disabled:opacity-50;
  }

  .btn-primary {
    @apply btn bg-ink text-vellum rounded-full px-6 py-3
           hover:bg-ink/90 active:scale-[0.98];
  }

  .btn-secondary {
    @apply btn bg-card text-foreground border border-border rounded-full px-6 py-3
           hover:bg-muted active:scale-[0.98];
  }

  .btn-ghost {
    @apply btn text-foreground rounded-lg px-4 py-2
           hover:bg-muted/50 active:scale-[0.98];
  }

  .btn-gold {
    @apply btn bg-antique-gold text-white rounded-full px-6 py-3
           hover:bg-antique-gold/90 active:scale-[0.98]
           shadow-glow-gold;
  }

  .btn-sm {
    @apply text-sm px-4 py-2;
  }

  .btn-lg {
    @apply text-lg px-8 py-4;
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Card Styles
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .card {
    @apply bg-card text-card-foreground rounded-xl border border-border
           shadow-card transition-shadow duration-200;
  }

  .card:hover {
    @apply shadow-card-hover;
  }

  .card-header {
    @apply p-6 pb-0;
  }

  .card-title {
    @apply font-serif text-card-title font-semibold tracking-tight;
  }

  .card-content {
    @apply p-6;
  }

  /* Insight Card */
  .insight-card {
    @apply card border-l-4 shadow-insight;
    border-left-color: hsl(var(--skill-primary));
  }

  .insight-card[data-type="discovery"] {
    @apply border-l-element-water;
  }

  .insight-card[data-type="pattern"] {
    @apply border-l-zodiac-primary;
  }

  .insight-card[data-type="timing"] {
    @apply border-l-antique-gold;
  }

  .insight-card[data-type="growth"] {
    @apply border-l-element-wood;
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Input Styles
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .input {
    @apply w-full bg-card text-foreground rounded-lg px-4 py-3
           border border-border
           placeholder:text-muted-foreground
           focus:outline-none focus:ring-2 focus:ring-ring focus:border-transparent
           transition-all duration-200;
  }

  .input-capsule {
    @apply input rounded-full px-6;
  }

  .select {
    @apply input appearance-none cursor-pointer
           bg-[url('data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%2224%22%20height%3D%2224%22%20viewBox%3D%220%200%2024%2024%22%20fill%3D%22none%22%20stroke%3D%22%236D655B%22%20stroke-width%3D%222%22%20stroke-linecap%3D%22round%22%20stroke-linejoin%3D%22round%22%3E%3Cpath%20d%3D%22m6%209%206%206%206-6%22%2F%3E%3C%2Fsvg%3E')]
           bg-no-repeat bg-[right_1rem_center] bg-[length:1rem];
  }

  .label {
    @apply text-sm font-medium text-foreground mb-2 block;
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Chat Styles
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .chat-bubble {
    @apply max-w-[80%] rounded-2xl px-4 py-3 animate-fade-in-up;
  }

  .chat-bubble-user {
    @apply chat-bubble bg-ink text-vellum ml-auto rounded-br-sm;
  }

  .chat-bubble-assistant {
    @apply chat-bubble bg-card border border-border mr-auto rounded-bl-sm;
  }

  .chat-timestamp {
    @apply text-xs text-muted-foreground mt-1;
  }

  /* Omnibar */
  .omnibar {
    @apply glass-capsule flex items-center gap-3 px-4 py-3;
  }

  .omnibar-input {
    @apply flex-1 bg-transparent border-none outline-none
           text-foreground placeholder:text-muted-foreground
           resize-none;
  }

  .omnibar-btn {
    @apply p-2 rounded-full text-muted-foreground
           hover:bg-muted/50 hover:text-foreground
           transition-colors duration-200;
  }

  .omnibar-send {
    @apply p-2 rounded-full bg-ink text-vellum
           hover:bg-ink/90 active:scale-95
           transition-all duration-200;
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Insight Seal (å°ç« )
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .insight-seal {
    @apply relative inline-flex items-center justify-center
           w-12 h-12 rounded-full
           text-antique-gold font-serif text-xs font-bold
           border-2 border-antique-gold/30
           bg-gradient-to-br from-antique-gold/10 to-transparent;
    transform: rotate(-12deg);
  }

  .insight-seal::before {
    content: "";
    @apply absolute inset-1 rounded-full border border-antique-gold/20;
  }

  .insight-seal-lg {
    @apply w-16 h-16 text-sm;
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Breath Aura (å‘¼å¸å…‰æ™•)
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .breath-aura {
    @apply absolute rounded-full blur-3xl;
    background: var(--skill-glow);
    animation: breath var(--breath-duration) ease-in-out infinite;
  }

  .breath-aura-sm {
    @apply w-32 h-32;
  }

  .breath-aura-md {
    @apply w-48 h-48;
  }

  .breath-aura-lg {
    @apply w-64 h-64;
  }
}

/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   Vibe Glyph (å“ç‰Œå›¾æ ‡)
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .vibe-glyph {
    @apply relative flex items-center justify-center;
  }

  .vibe-glyph-inner {
    @apply relative z-10;
  }

  .vibe-glyph-aura {
    @apply absolute inset-0 breath-aura rounded-full;
  }
