   Card Styles
   â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
@layer components {
  .card {
    @apply bg-card text-card-foreground rounded-xl border border-border
           shadow-card transition-shadow duration-200;
  }

  .card:hover {
    @apply shadow-card-hover;
  }

  .card-header {
    @apply p-6 pb-0;
  }

  .card-title {
    @apply font-serif text-card-title font-semibold tracking-tight;
  }

  .card-content {
    @apply p-6;
  }

  /* Insight Card */
  .insight-card {
    @apply card border-l-4 shadow-insight;
    border-left-color: hsl(var(--skill-primary));
  }

  .insight-card[data-type="discovery"] {
    @apply border-l-element-water;
  }

  .insight-card[data-type="pattern"] {
    @apply border-l-zodiac-primary;
  }

  .insight-card[data-type="timing"] {
    @apply border-l-antique-gold;
