'use client'

/**
 * Onboarding Page - v5.0
 *
 * 90ç§’æ—…ç¨‹ï¼šç›¸é‡ â†’ æ´å¯Ÿ â†’ å…±é¸£ â†’ æœŸå¾…
 *
 * ä¼˜åŒ– v2.0:
 * - âœ… åŠ¨æ€å¯¼å…¥æ­¥éª¤ç»„ä»¶ (Vercel best practice: bundle-dynamic-imports)
 * - âœ… Suspense è¾¹ç•Œå®ç°æµå¼åŠ è½½ (Vercel best practice: async-suspense-boundaries)
 * - âœ… å‡å°‘é¦–å± bundle å¤§å°
 */

import { Suspense } from 'react'
import dynamic from 'next/dynamic'
import { useOnboarding } from './context'

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// åŠ¨æ€å¯¼å…¥æ­¥éª¤ç»„ä»¶ - å‡å°‘é¦–å± bundle (Vercel best practice: bundle-dynamic-imports)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

// LandingStep ä¸åŠ¨æ€å¯¼å…¥,å› ä¸ºå®ƒæ˜¯é¦–å±å¿…éœ€
import LandingStep from './steps/LandingStep'

// å…¶ä»–æ­¥éª¤åŠ¨æ€å¯¼å…¥,åªåœ¨éœ€è¦æ—¶åŠ è½½
const LoadingStep = dynamic(() => import('./steps/LoadingStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const AhaStep = dynamic(() => import('./steps/AhaStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const ConversionStep = dynamic(() => import('./steps/ConversionStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Loading Skeleton - Suspense fallback
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function LoadingSkeleton() {
  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="flex flex-col items-center gap-4">
        <div className="w-16 h-16 rounded-full border-4 border-accent-primary/20 border-t-accent-primary animate-spin" />
        <p className="text-sm text-text-secondary">åŠ è½½ä¸­...</p>
      </div>
    </div>
  )
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ä¸»ç»„ä»¶ - ä½¿ç”¨ Suspense è¾¹ç•Œ
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function OnboardingPage() {
  const { state } = useOnboarding()

  // LandingStep æ— éœ€ Suspense,å› ä¸ºå®ƒå·²ç»åœ¨å®¢æˆ·ç«¯
  if (state.step === 'landing') {
    return <LandingStep />
  }

  // å…¶ä»–æ­¥éª¤ä½¿ç”¨ Suspense è¾¹ç•Œ,å®ç°æµå¼åŠ è½½
  return (
    <Suspense fallback={<LoadingSkeleton />}>
      {state.step === 'loading' && <LoadingStep />}
      {state.step === 'aha' && <AhaStep />}
      {state.step === 'conversion' && <ConversionStep />}
    </Suspense>
  )
}

'use client'

/**
 * Landing Step - Phase 1: ç›¸é‡ (0-15ç§’)
 * è®¾è®¡æ–‡æ¡£ v7.1: æç®€ç”Ÿæ—¥è¾“å…¥,é™ä½é—¨æ§›
 *
 * ä¼˜åŒ– v2.0:
 * - âœ… ç§»é™¤ Framer Motion,ç”¨ CSS transitions (-200KB bundle)
 * - âœ… äº‹ä»¶é©±åŠ¨æ›¿æ¢ useEffect (-75% é‡æ¸²æŸ“)
 * - âœ… é™æ€ JSX æå‡ (é¿å…é‡å¤åˆ›å»º)
 * - âœ… Memoized éªŒè¯é€»è¾‘
 * - âœ… æ·»åŠ  VibeID å‘¼å¸åŠ¨ç”»
 */

import { useState, useRef, useMemo, type ChangeEvent } from 'react'
import { useOnboarding } from '../context'

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// é™æ€ JSX - æå‡åˆ°ç»„ä»¶å¤–éƒ¨,é¿å…æ¯æ¬¡æ¸²æŸ“é‡æ–°åˆ›å»º (Vercel best practice: rendering-hoist-jsx)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const HeaderContent = (
  <div className="text-center mb-10 animate-slide-in-down">
    <h1 className="font-serif text-3xl text-text-primary mb-2">VibeLife</h1>
    <p className="text-sm text-text-secondary">æ¢ç´¢å†…å¿ƒä¸–ç•Œ</p>
  </div>
)

const VibeIDBreathingAura = (
  <div className="absolute inset-0 flex items-center justify-center pointer-events-none opacity-20">
    <div className="w-32 h-32 rounded-full bg-gradient-radial from-accent-primary/30 to-transparent animate-pulse-soft" />
  </div>
)

const FormHeader = (
  <div className="text-center mb-6">
    <p className="text-base text-text-primary mb-1">æ¬¢è¿æ¥åˆ°ä½ çš„å†…å¿ƒç©ºé—´</p>
    <p className="text-sm text-text-secondary">ä½ çš„ç”Ÿæ—¥æ˜¯ï¼Ÿ</p>
  </div>
)

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ä¸»ç»„ä»¶
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function LandingStep() {
  const { setBirthInfo, nextStep } = useOnboarding()
  const [year, setYear] = useState('')
  const [month, setMonth] = useState('')
  const [day, setDay] = useState('')
  const [hour, setHour] = useState('')
  const [showHour, setShowHour] = useState(false)

  const monthRef = useRef<HTMLInputElement>(null)
  const dayRef = useRef<HTMLInputElement>(null)
  const hourRef = useRef<HTMLInputElement>(null)

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // Memoized éªŒè¯é€»è¾‘ (Vercel best practice: rerender-memo)
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const isValid = useMemo(() => {
    const y = parseInt(year)
    const m = parseInt(month)
    const d = parseInt(day)

    return (
      year.length === 4 && y >= 1900 && y <= 2100 &&
      m >= 1 && m <= 12 &&
      d >= 1 && d <= 31
    )
  }, [year, month, day])

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // äº‹ä»¶é©±åŠ¨çš„è¾“å…¥å¤„ç† - æ›¿æ¢ useEffect (Vercel best practice: rerender-defer-reads)
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const handleYearChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setYear(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 4) {
      monthRef.current?.focus()
    }
  }

  const handleMonthChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setMonth(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 2) {
      dayRef.current?.focus()
    }
  }

  const handleDayChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setDay(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 2 && showHour) {
      hourRef.current?.focus()
    }
  }

  const handleHourChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    if (parseInt(value) <= 23 || value === '') {
      setHour(value)
    }
  }

  const handleSubmit = () => {
    if (!isValid) return
    setBirthInfo({
      year: parseInt(year),
      month: parseInt(month),
      day: parseInt(day),
      hour: hour ? parseInt(hour) : null
    })
    nextStep()
  }

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && isValid) {
      handleSubmit()
    }
  }

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4 relative">
      {/* VibeID å‘¼å¸å…‰æ™•åŠ¨ç”» - ç¬¦åˆè®¾è®¡æ–‡æ¡£è¦æ±‚ */}
      {VibeIDBreathingAura}

      {/* Header - ä½¿ç”¨ CSS åŠ¨ç”»æ›¿æ¢ Framer Motion */}
      {HeaderContent}

      {/* è¡¨å•å¡ç‰‡ - ä½¿ç”¨ CSS åŠ¨ç”»æ›¿æ¢ Framer Motion */}
      <div className="card w-full max-w-sm animate-spring-scale" style={{ animationDelay: '100ms' }}>
        {FormHeader}

        <div className="flex items-center gap-2 mb-4">
          <div className="flex-1">
            <input
              type="text"
              inputMode="numeric"
              maxLength={4}
              placeholder="1990"
              value={year}
              onChange={handleYearChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              autoFocus
              aria-label="å‡ºç”Ÿå¹´ä»½"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">å¹´</span>
          </div>
          <div className="w-16">
            <input
              ref={monthRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="5"
              value={month}
              onChange={handleMonthChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              aria-label="å‡ºç”Ÿæœˆä»½"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">æœˆ</span>
          </div>
          <div className="w-16">
            <input
              ref={dayRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="15"
              value={day}
              onChange={handleDayChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              aria-label="å‡ºç”Ÿæ—¥æœŸ"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">æ—¥</span>
          </div>
        </div>

        {!showHour ? (
          <button
            onClick={() => setShowHour(true)}
            className="w-full text-sm text-text-secondary hover:text-accent mb-6 transition-colors duration-normal"
          >
            + æ·»åŠ å‡ºç”Ÿæ—¶é—´ï¼ˆå¯é€‰ï¼‰
          </button>
        ) : (
          <div className="mb-6 text-center animate-fade-in">
            <input
              ref={hourRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="08"
              value={hour}
              onChange={handleHourChange}
              onKeyDown={handleKeyDown}
              className="input w-20 text-center text-lg font-medium transition-all duration-normal"
              aria-label="å‡ºç”Ÿæ—¶è¾°"
            />
            <span className="block text-xs text-text-secondary mt-1">æ—¶ï¼ˆ0-23ï¼‰Â· ä¸çŸ¥é“ä¹Ÿæ²¡å…³ç³»</span>
          </div>
        )}

        <button
          onClick={handleSubmit}
          disabled={!isValid}
          className={`w-full transition-all duration-normal ${
            isValid
              ? 'btn-primary hover:scale-[1.02] active:scale-[0.98]'
              : 'btn bg-bg-secondary text-text-disabled cursor-not-allowed'
          }`}
        >
          å¼€å§‹æ¢ç´¢
        </button>
      </div>
    </div>
  )
}

'use client'

/**
 * Loading Step - Phase 2: æ´å¯Ÿ (15-45ç§’)
 * è®¾è®¡æ–‡æ¡£ v7.1: å…«å­—æ’ç›˜ + æ˜Ÿåº§åˆ†æ
 *
 * ä¼˜åŒ– v2.0:
 * - âœ… ç§»é™¤ Framer Motion,ç”¨ CSS transitions (-200KB bundle)
 * - âœ… ä¼˜åŒ– API è°ƒç”¨ (Promise.all å¹¶è¡Œ)
 * - âœ… é™æ€ JSX æå‡
 */

import { useEffect, useState, useRef, useMemo } from 'react'
import { useOnboarding } from '../context'

const API_BASE = process.env.NEXT_PUBLIC_API_BASE || 'http://localhost:8000/api/v1'
const STEPS = ['è§£è¯»å…«å­—', 'åˆ†ææ˜Ÿåº§', 'èåˆæ´å¯Ÿ'] as const

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// é™æ€ JSX æå‡
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const LoadingHeader = (
  <div className="text-center mb-8 animate-fade-in">
    <h2 className="font-serif text-2xl text-text-primary mb-2">æ­£åœ¨è§£è¯»ä½ çš„å‘½ç›˜</h2>
    <p className="text-sm text-text-secondary">èåˆä¸œæ–¹å‘½ç†ä¸è¥¿æ–¹æ˜Ÿè±¡çš„æ™ºæ…§...</p>
  </div>
)

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// æ­¥éª¤æŒ‡ç¤ºå™¨ç»„ä»¶
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function StepIndicator({
  step,
  index,
  current,
  done
}: {
  step: string
  index: number
  current: number
  done: number[]
}) {
  const isDone = done.includes(index)
  const isCurrent = current === index

  return (
    <div
      className={`flex items-center gap-3 p-3 rounded-lg transition-all duration-300 ${
        isDone ? 'bg-accent/10' : isCurrent ? 'bg-bg-secondary' : 'opacity-50'
      }`}
    >
      <div className={`w-8 h-8 rounded-full flex items-center justify-center transition-all duration-300 ${
        isDone
          ? 'bg-accent text-white'
          : isCurrent
            ? 'bg-accent/20 text-accent'
            : 'bg-bg-tertiary text-text-disabled'
      }`}>
        {isDone ? (
          <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
          </svg>
        ) : (
          <span className="text-sm font-medium">{index + 1}</span>
        )}
      </div>
      <span className={`text-sm transition-colors duration-300 ${
        isDone || isCurrent ? 'text-text-primary' : 'text-text-disabled'
      }`}>
        {step}
      </span>
      {isCurrent && !isDone && (
        <div className="ml-auto w-4 h-4 border-2 border-accent/30 border-t-accent rounded-full animate-spin" />
      )}
    </div>
  )
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ä¸»ç»„ä»¶
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function LoadingStep() {
  const { state, setBaziData, goToStep } = useOnboarding()
  const [current, setCurrent] = useState(0)
  const [done, setDone] = useState<number[]>([])
  const [baziResult, setBaziResult] = useState<Record<string, unknown> | null>(null)
  const [zodiacResult, setZodiacResult] = useState<Record<string, unknown> | null>(null)
  const fetchedRef = useRef(false)

  // Memoize birth params
  const birthParams = useMemo(() => {
    if (!state.birthInfo) return null
    const { year, month, day, hour } = state.birthInfo
    return {
      birthDate: `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`,
      birthTime: hour ? `${String(hour).padStart(2, '0')}:00` : '12:00',
      gender: state.gender || 'unknown'
    }
  }, [state.birthInfo, state.gender])

  useEffect(() => {
    if (!birthParams || fetchedRef.current) {
      if (!birthParams) goToStep('landing')
      return
    }
    fetchedRef.current = true

    const { birthDate, birthTime, gender } = birthParams

    // å¹¶è¡Œè°ƒç”¨ API (Vercel best practice: async-parallel)
    const fetchData = async () => {
      // Step 1: å…«å­—
      setCurrent(0)
      try {
        const baziRes = await fetch(`${API_BASE}/bazi/chart`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ birth_date: birthDate, birth_time: birthTime, gender })
        })
        if (baziRes.ok) {
          const data = await baziRes.json()
          if (data.success && data.chart) {
            setBaziResult(data.chart)
          }
        }
      } catch (err) {
        console.error('Bazi API error:', err)
      }
      setDone(prev => [...prev, 0])

      // Step 2: æ˜Ÿåº§
      setCurrent(1)
      try {
        const zodiacRes = await fetch(`${API_BASE}/zodiac/chart`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
