    EXTREMELY_STRONG = "ææ—º"  # å¾—ä»¤å¾—åœ°å¾—åŠ¿ï¼Œéœ€è¦æ³„è€—
    STRONG = "åæ—º"            # æœ‰æ ¹æœ‰æ°”ï¼Œå–œæ³„è€—
    BALANCED = "ä¸­å’Œ"          # èº«æ—ºå¼±å¹³è¡¡ï¼Œå–œé¡ºåŠ¿
    WEAK = "åå¼±"              # æ— æ ¹å°‘æ°”ï¼Œå–œç”Ÿæ‰¶
    EXTREMELY_WEAK = "æå¼±"    # ä»æ ¼æ¡ä»¶ï¼Œéœ€ç‰¹æ®Šå¤„ç†

def calculate_day_master_strength(bazi: BaziChart) -> tuple[DayMasterStrength, float]:
    """
    è®¡ç®—æ—¥ä¸»å¼ºå¼±

    Returns:
        (å¼ºå¼±ç­‰çº§, å¼ºåº¦åˆ†æ•° -1.0 åˆ° 1.0)
    """
    score = 0.0

    # 1. å¾—ä»¤åˆ¤æ–­ (æœˆæ”¯å¯¹æ—¥ä¸»çš„å½±å“)
    month_branch = bazi.month_branch
    day_master = bazi.day_master
    seasonal_factor = get_seasonal_strength(day_master, month_branch)
    score += seasonal_factor * 0.4  # æƒé‡40%

    # 2. å¾—åœ°åˆ¤æ–­ (å››æ”¯å¯¹æ—¥ä¸»çš„æ ¹æ°”)
    root_factor = calculate_root_strength(day_master, bazi.branches)
    score += root_factor * 0.3  # æƒé‡30%

    # 3. å¾—åŠ¿åˆ¤æ–­ (å¤©å¹²é€å‡ºçš„å¸®æ‰¶)
    support_factor = calculate_stem_support(day_master, bazi.stems)
    score += support_factor * 0.2  # æƒé‡20%

    # 4. åˆå†²ä¿®æ­£ (å¹²æ”¯åˆåŒ–ã€åœ°æ”¯å†²åˆ)
    combination_factor = calculate_combination_effect(bazi)
    score += combination_factor * 0.1  # æƒé‡10%

    # è½¬æ¢ä¸ºç­‰çº§
    if score >= 0.6:
        return (DayMasterStrength.EXTREMELY_STRONG, score)
    elif score >= 0.2:
        return (DayMasterStrength.STRONG, score)
    elif score >= -0.2:
        return (DayMasterStrength.BALANCED, score)
    elif score >= -0.6:
        return (DayMasterStrength.WEAK, score)
    else:
        return (DayMasterStrength.EXTREMELY_WEAK, score)
