    zodiac_primary: Archetype,
    consistency: str,
    bazi_explanation: list[str],
    zodiac_explanation: list[str]
) -> dict:
    """
    ç”Ÿæˆç”¨æˆ·å¯ç†è§£çš„åŸå‹è§£é‡Š
    """

    explanation = {
        "summary": "",
        "bazi_insight": "",
        "zodiac_insight": "",
        "integration_insight": "",
        "growth_direction": ""
    }

    # ä¸»åŸå‹æ€»ç»“
    explanation["summary"] = f"ä½ çš„æ ¸å¿ƒåŸå‹æ˜¯ã€{primary.chinese_name}ã€‘ï¼Œå‰¯åŸå‹æ˜¯ã€{secondary.chinese_name}ã€‘"

    # å…«å­—æ´å¯Ÿ
    explanation["bazi_insight"] = (
        f"ä»å…«å­—è§’åº¦çœ‹ï¼Œ{bazi_explanation[0]}ã€‚"
        f"è¿™å†³å®šäº†ä½ çš„äººç”Ÿåº•è‰²æ˜¯{bazi_primary.chinese_name}å‹çš„ã€‚"
    )

    # æ˜Ÿåº§æ´å¯Ÿ
    explanation["zodiac_insight"] = (
        f"ä»æ˜Ÿç›˜è§’åº¦çœ‹ï¼Œ{'; '.join(zodiac_explanation[:3])}ã€‚"
        f"è¿™å½±å“äº†ä½ çš„è¡¨è¾¾æ–¹å¼åå‘{zodiac_primary.chinese_name}ã€‚"
    )

    # èåˆæ´å¯Ÿ
    if consistency == "perfect_match":
        explanation["integration_insight"] = (
            f"ä¸œæ–¹å‘½ç†ä¸è¥¿æ–¹å æ˜Ÿé«˜åº¦ä¸€è‡´åœ°æŒ‡å‘{primary.chinese_name}åŸå‹ï¼Œ"
            f"è¯´æ˜è¿™æ˜¯ä½ éå¸¸ç¨³å®šçš„æ ¸å¿ƒç‰¹è´¨ã€‚"
        )
    elif consistency == "conflict":
        explanation["integration_insight"] = (
            f"æœ‰è¶£çš„æ˜¯ï¼Œå…«å­—æŒ‡å‘{bazi_primary.chinese_name}ï¼Œ"
            f"è€Œæ˜Ÿç›˜æŒ‡å‘{zodiac_primary.chinese_name}ï¼Œ"
            f"è¿™ç§å¼ åŠ›å¯èƒ½è®©ä½ å†…å¿ƒæœ‰æ—¶æ„Ÿåˆ°çŸ›ç›¾ã€‚"
            f"æœ€ç»ˆæˆ‘ä»¬ä»¥å…«å­—ä¸ºä¸»ï¼Œç¡®å®šä½ çš„ä¸»åŸå‹æ˜¯{primary.chinese_name}ã€‚"
        )
    else:
        explanation["integration_insight"] = (
            f"å…«å­—çš„{bazi_primary.chinese_name}ç‰¹è´¨ä¸æ˜Ÿç›˜çš„{zodiac_primary.chinese_name}ç‰¹è´¨"
            f"ç›¸äº’è¡¥å……ï¼Œè®©ä½ çš„äººæ ¼æ›´åŠ ä¸°å¯Œå¤šå…ƒã€‚"
        )

    # æˆé•¿æ–¹å‘
    explanation["growth_direction"] = generate_growth_direction(primary, secondary)

    return explanation

def generate_growth_direction(primary: Archetype, secondary: Archetype) -> str:
    """
